vec4 cell(ivec2 f) {
    return texelFetch(iChannel0, f, 0);
}

vec4 ncell(ivec2 f) {
    return texelFetch(iChannel1, f, 0);
}

void mainImage(out vec4 col, in vec2 f)
{
    col = vec4(0.0);
    vec4 ncol, ncol2 = vec4(0.0);

    vec2 uv = (f - 0.5 * iResolution.xy) / iResolution.y;
    f.xy += 2. * cos(f.x / 80. + 0.5 * iTime);

    // Translate and zoom
    f -= 0.5 * iResolution.xy;
    f *= 2. * dim.y/iResolution.y; 
    f += vec2(0.5,1) * dim;

    // Mirrored coords 
    float s = step(floor(f.x), 0.5 * dim.x);   
    ivec2 ff = ivec2(s * dim.x + (1. - f.x) * (2. * s - 1.), f.y);
    col = cell(ff);
    vec4 col2 = cell(ff + ivec2(1,0)); // was 0,1

    vec3 pl = pal(4. * col.r, vec3(0,1,1)/3.);
    col.rgb = (1. - step(col.r, 0.)) * pl;
    col.rgb += 0.5 * col.r * pal(1.5, vec3(0,1,2)/3.);
    col.rgb += 0.5 * col2.r * pl;
    float csh = 1. / cosh((10. - 8.8 * tanh(iTime)) * max(abs(uv.x), abs(uv.y)));
    col = mix(col, vec4(0.5,0.5 + 0.5 * cos(0.25 * uv.y + iTime),1,1), 1.-csh);

    ff.y -= int((1.-tanh(0.4 * iTime)) * 100.);
    ncol = ncell(ff);
    ncol2 = ncell(ff + ivec2(1,0)); 

    vec3 npl = pal(4. * ncol.r, vec3(0,1,1)/3.);
    ncol.rgb = (1. - step(ncol.r, 0.)) * npl;
    ncol.rgb += 0.5 * ncol.r * pal(1.5, vec3(0,1,2)/3.);
    ncol.rgb += 0.5 * ncol2.r * npl;
    ncol = mix(ncol, vec4(0.5,0.5 + 0.5 * cos(0.25 * uv.y + iTime),1,1), 1.-csh);

    if (ff.y > 0 && ff.y < int(0.58 * dim.y))
        col = ncol;
    //col = mix(col, ncol, 0.5 + 0.5 * thc(4., 8. * uv.x + 0.4 * iTime));

}

vec4 cell(in ivec2 p) {
    if (iFrame == 0) // maybe this fixes uninitialized vectors?
        return vec4(0.0);

    ivec2 r = ivec2(textureSize(iChannel0, 0));
    p = (p+r) % r;
    return texelFetch(iChannel0, p, 0 );
}

void mainImage(out vec4 col, in vec2 f)
{
    col = vec4(0.0);
    ivec2 px = ivec2(f);
    int frame = iFrame;//iFrame % 120;

    // not my finest line of code!
    int h = 3 + int(floor(9. * h21(vec2(floor(float(iFrame)/120.), 19.))));

    float rand = h21(f / iResolution.y + vec2(0.1, 0.151) * mod(iDate[3], 5.));

    if (frame == 0) {     
        col = vec4(0.0, step(0.9, rand), 0.0, 1.0);
        return;
    }

    // Center cell
    vec4 e = vec4(0.0);
    e = cell(px);
    col = e;

    // Neighbour cells
    vec4 b = cell(px - ivec2(0,1));
    vec4 t = cell(px + ivec2(0,1));
    vec4 l = cell(px - ivec2(1,0));
    vec4 r = cell(px + ivec2(1,0));   
    vec4 bl = cell(px + ivec2(-1,-1));
    vec4 br = cell(px + ivec2(1, -1));
    vec4 tl = cell(px + ivec2(-1, 1));
    vec4 tr = cell(px + ivec2(1,  1));   

    vec4 bb = cell(px - ivec2(0,2));
    vec4 tt = cell(px + ivec2(0,2));
    vec4 ll = cell(px - ivec2(2,0));
    vec4 rr = cell(px + ivec2(2,0));  

    vec4 sum = b + t + l + r;
    vec4 diag = bl + br + tl + tr;
    vec4 dif = abs(b-t) * abs(l-r);
    vec4 dif2 = (t + b) * (l + r);
    vec4 diDif = (tl + br) * (tr + bl);

    if (frame < 10) {
        col.g = 0.4 * diag.g - e.g;
        //col.g = 0.3 * diag.g + 0.2 * sum.g - e.g;
    } else if (frame == 10) {
        col.g = step(0.01, e.g);
    } else if (frame < 13) {
        if (f.x < 0.5 * dim.x + 1. && abs(f.y - 0.5 * dim.y) < 4.) //could replace 4 with random val
           col.r = 1.;
    } else if (frame == 13) {
        col.b = e.g;
     //  if (e.g == 1. && dif2.g == 1.)
      //     col.g = 0.;
    } else { 
        //if (e.g < 0.5 && sum.r >0.5)
        //    e.g = 1.;
        if (e.g == 1. && col.r < 0.2 && (sum+diag).r > 0.5)
            col.r = 1.;

        col.r += 0.005 * ((sum+diag).r/8. - 1.);

        float v = step(tl.r, 0.) + step(tr.r,0.) + 
                  step(bl.r,0.) + step(br.r,0.);
        if (e.g < 1. && (sum).r >= 2. && v <= 1.)
            col.g = 1.;

        if (col.g > 0. && dif.r != 0.) {
            col.g = 0.;
        }

        col.rg = clamp(col.rg, 0., 1.);
    } 

}

#define pi 3.14159
#define dim vec2(64, 52)

#define thc(a,b) tanh(a*cos(b))/tanh(a)

vec3 pal(in float t, in vec3 d) {
    return 0.5 + 0.5 * cos(2. * pi * (0.5 * t + d));
}

float h21(vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}
