#define pi 3.1415927
#define _2pi 2.0*pi

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    float t = (cos(iTime*_2pi)*0.5+0.65)*10.;
    //float t = (iMouse.y/iResolution.y)*10.;
    float dx = mod(floor(uv.x*t+0.5), 2.);
    float dy = mod(floor(uv.y*t+0.5), 2.);
    vec3 col = vec3(mod(dx+dy, 2.));
    if (floor(uv*t+0.5) == vec2(0)) {
        col = vec3(0, 0.5, 0);
    }


    float t2 = iTime/6.0;
    float t3 = smoothstep(0.4, 0.6, abs(sin(t2*pi)));
    col = mix(col, vec3(0, 0.5, 0), 1.0-t3);

    // Output to screen
    fragColor = vec4(col,1.0);
}
