void mainImage(out vec4 O, vec2 I)
{
    O -= O;
    vec2 c,r = iResolution.xy;
    for(int i=0; i++<400;
    O += ((i+int(iTime))%9<7?vec4(i%2,1-i&1,0,1):vec4(1))/
    dot(c = (I+I-r)/r.y*3e2+vec2(0,i-230)+vec2(i+i%99,i/4)*cos(iTime*.2+vec2(i,i+11))*.5,c));
    O = sqrt(O/.1);
}
