bool green_background = true;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 mouse = iMouse.xy/iResolution.xy;
    vec2 mouse2 = iMouse.zw/iResolution.xy;

    // Time varying pixel color
    float light = length(mouse2-mouse);
    vec3 col = texture(iChannel0, vec2(uv.x, uv.y)).rgb;
    vec3 background = texture(iChannel1, vec2(uv.x-iTime, uv.y)).rgb;
    float rho = length(vec2(uv.x*1.8, uv.y)-vec2(mouse.x*1.8, mouse.y));
    if (mouse == vec2(0.0)) {
        col = col;
    }
    else {
        col *= (1.0-rho)*light*light;
    }
    if (rho < light/10.0) {col = vec3(3);}
    //vec3 test = vec3(sin(iTime));
    if (green_background) {
        if (col.g+0.08 > col.r && col.g-0.07 > col.b) {col = background;}
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
