vec2 R22(vec2 p) 
{
    vec3 a = fract(p.xyx * vec3(2.34, 5.34, 123.65));
    a += dot(a, a + 34.45);
    return fract(vec2(a.x*a.y, a.y*a.z));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;    
    uv *= 10.;

    vec3 col = vec3(.0);

    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);

    float minDist = 1.;

    for (float y = -4.; y <= 4.; y ++)
    {
        for(float x = -4.; x <= 4.; x++)
        {

            vec2 offset = vec2(x,y);
            vec2 n = R22(id + offset);
            vec2 p = offset+sin(n*iTime)*2.;

            float dist = length(gv-p);

            if(dist < minDist)
            {
                minDist = dist;
            }
        }
    }

    col += cos(minDist)*.8;
    //col += .04/minDist;

    fragColor = vec4(col,1.0);
}
