vec3 white = vec3(1.);
vec3 black = vec3(0.);

float col0(in vec2 _st, in float _width) {
    return smoothstep(0.98+_width/2.,0.98-_width/2.,_st.y);
}
float col1(in vec2 _st, in float _width) {
    return smoothstep(0.,0.+_width,_st.y);
}

float row0(in vec2 _st, in float _width) {
    return smoothstep(0.98+_width/2.,0.98-_width/2.,_st.x);
}

float row1(in vec2 _st, in float _width) {
    return smoothstep(0.,0.+_width,_st.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy;

    vec3 color = vec3(0.0);
    st *= 3.0;      // Scale up the space by 3
    st = fract(st); // Wrap around 1.0

    float row = min(row0(st,0.02),row1(st,0.02));
    float col = min(col0(st,0.02),col1(st,0.02));
    float pct = min(col,row);
    color = vec3(pct);

    fragColor = vec4(color,1.0);
}
