#define MAX_ITER 32
#define TIMESCALE .5
#define THRESHOLD 2.

// Color scheme lifted from the default colors in Ultra Fractal
vec3 color(int iter){
    iter = iter % 16;
    vec3 colors[16] = vec3[16](
        vec3(0., 0., 0.),
        vec3(25., 7., 26.),
        vec3(9., 1., 47.),
        vec3(4., 4., 73.),
        vec3(0., 7., 100.),
        vec3(12., 44., 138.),
        vec3(24., 82., 177.),
        vec3(57., 125., 209.),
        vec3(134., 181., 229.),
        vec3(211., 236., 248.),
        vec3(241., 233., 191.),
        vec3(248., 201., 95.),
        vec3(255., 170., 0.),
        vec3(204., 128., 0.),
        vec3(153., 87., 0.),
        vec3(106., 52., 3.)
    );
return colors[iter] / 255.;
}

// Iterate quadratic Julia set, return number of iterations before convergence (or divergence!)
int julia(vec2 z){
    int iter = 0;
    vec2 mouseuv = iMouse.xy/iResolution.xy;
    vec2 c = (mouseuv - 0.5) * 2.;
    c *= vec2(sin(iTime* TIMESCALE), cos(iTime* TIMESCALE));
    while (iter < MAX_ITER){
        float re = z.x*z.x - z.y*z.y;
        z.y = 2.*z.x*z.y;
        z.x = re;
        z += c;
        if (length(z)>THRESHOLD) break;
        iter++;
    }
    return iter;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // y in [-3,3], x somewhere like [-5.3, 5.3]
    vec2 uv = (fragCoord - iResolution.xy/2.) / iResolution.y * 3.;

    // Output to screen
    fragColor = vec4(color(julia(uv)), 1.);
}
