#define t iTime
#define r iResolution.xy



vec4 circle(vec2 uv, vec2 pos, float rad, vec3 color) {
	float d = length(pos - uv) - rad;
	float t = clamp(d, 0.0, 1.0);
	return vec4(color, 1.0 - t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    int pool_size = 10;
    vec2 fragCoodStep = vec2(float(int(fragCoord.x/float(pool_size)))*float(pool_size), float(int(fragCoord.y/float(pool_size)))*float(pool_size));

    // don't overindex
    if ( (fragCoord.x < (iResolution.x-float(pool_size))) && (fragCoord.y < (iResolution.y-float(pool_size))) ){

        // average pooling
        vec4 b = vec4(0);
        for(int i=0;i<pool_size;i++) {
            for(int k=0;k<pool_size;k++) {
                b = b + (texture(iChannel0, vec2(fragCoodStep.x+float(i), fragCoodStep.y+float(k))/iResolution.xy));
            }
        }
        b /= float(pool_size*pool_size);

        // current circle size
        float s = (b.x + b.y + b.z)/3.0;

        // Background layer
        vec4 layer1 = vec4(0.0, 0.0, 0.0, 1.0);
        // Circle
        vec3 cicrle_color = vec3(b.x, b.y, b.z);
        vec4 layer2 = circle(fragCoord.xy, fragCoodStep+float(pool_size)/2., float(pool_size)/2.0*s, cicrle_color);

        // Blend the two
        fragColor = mix(layer1, layer2, layer2.a);
   }
}
