mat2 rot(float a){
	return mat2(cos(a), sin(a), -sin(a), cos(a));
}

float map(vec3 p) {
	return length(mod(p, 2.0) - 1.0) - (1.0 / 1.61666);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ( 2.0 * fragCoord - iResolution.xy ) / min(iResolution.x, iResolution.y);
	vec3 pos = vec3(uv * 5.0, -10.0);
    vec3 dir = vec3(0.0, 0.0, 1.0);
	pos.xy += iTime * 0.5;
    dir.xz *= rot(1.0 * 3.141592 / 180.0); //tr
	dir.yz *= rot(1.0 * 3.141592 / 180.0);
	float t = 0.0;
    for(int i = 0 ; i < 128; i++) {
        t += map(dir * t + pos);
    }
	fragColor = vec4(0.01 * t * vec3(1,2,3) ,1);
}
