// Permission is hereby granted, free of charge, to any person obtaining a copy of this software and associated documentation files (the "Software"), to deal in the Software without restriction, including without limitation the rights to use, copy, modify, merge, publish, distribute, sublicense, and/or sell copies of the Software, and to permit persons to whom the Software is furnished to do so, subject to the following conditions: The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software. THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS IN THE SOFTWARE.

// A list of useful distance function to simple primitives. All
// these functions (except for ellipsoid) return an exact
// euclidean distance, meaning they produce a better SDF than
// what you'd get if you were constructing them from boolean
// operations.

/* ---------------------------------------- */
// CTSS parameters

#define MAX_AA_FACTOR 4

// 1 for single sample, 2 for 2x2 supersampling, etc.
#define _AA_FACTOR 1
// #define _AA_FACTOR 2
// #define _AA_FACTOR 3

/* ---------------------------------------- */
// CTSS parameters

// #define NO_CTSS // disables CTSS

#define CTSS_NUM_SAMPLES 8 // maximum number of CTSS samples per pixel

#define USE_CTSS_WEIGHTED // toggle for using visibility-based weights; uncomment for uniform sample weights
// #define USE_CTSS_R // use CTSS Relaxed, faster approximation with sphere tracing termination at hard hit (not full hit)

// small value epsilon, precision for cone tracing intersections.
// Note: cone tracing eps=0.01 is similar to sphere tracing eps=0.0001
// min ~0.0005, else may have visual artifacts
#define OCCLUSION_EPS 0.01

#define OCCLUSION_SOFT (0. - OCCLUSION_EPS) // cone occlusion for soft hit detection
#define OCCLUSION_HARD (0.5 - OCCLUSION_EPS) // cone occlusion for hard hit detection

#ifdef USE_CTSS_R
#define OCCLUSION_STOP OCCLUSION_HARD // relaxed CTSS (faster, worse AA quality)
#else
#define OCCLUSION_STOP (1.0 - OCCLUSION_EPS) // full CTSS (slower, better AA quality)
#endif

#define MIN_SAMPLE_WEIGHT 0.01 // minimum allowed value for a soft hit group weight

#define SPHERE_TRACE_STEPSIZE_MULT 1.0 // controls step size as a multiple of SDF value, useful for non-ideal SDFs
#define CONE_RAD_STEP_MULT 0.5 // upper bound for the step size as a multiple of local cone Radius

/* ---------------------------------------- */

//#define USE_SUBPIXEL_EDGE_RESOLVE

// the following only apply when #DEBUG is defined
#define _USE_SUBPIXEL_EDGE_RESOLVE true
#define _SUBPIXEL_EDGE_RESOLVE_TWO_PLANE_INTERSECTION true
#define _SUBPIXEL_EDGE_RESOLVE_CONE_MULT_COS 2.0
#define _SUBPIXEL_EDGE_RESOLVE_CONE_MULT 1.0
#define _SUBPIXEL_EDGE_RESOLVE_COSSIM_THRESHOLD_MAX 0.999
#define _SUBPIXEL_EDGE_RESOLVE_COSSIM_THRESHOLD_MIN 0.9
#define _SUBPIXEL_EDGE_RESOLVE_CURVATURE_THRESHOLD 0.00001

/* ---------------------------------------- */
// Sphere tracing parameters

#define TMIN 0.01
#define TMAX 30.

#define _MAX_RAYMARCH_STEPS 2048  // max SDF marching steps

// cone radius expansion factor in units of pixel size
#define PIXEL_SIZE_MULT_INSCRIBED (1. / 2.) // cone is inscribed in the pixel (inside the pixel)
#define PIXEL_SIZE_MULT_CIRCUMSCRIBED (1. / sqrt(2.)) // cone is circumscribed in the pixel (intersects pixel corners)
#define PIXEL_SIZE_MULT_BALANCED (1. / ((2. + sqrt(2.)) / 2.)) // half way between inscribed and circumscribed
#define PIXEL_SIZE_MULT PIXEL_SIZE_MULT_BALANCED

/* ---------------------------------------- */

// CONE_BACKTRACE more correctly estimates the entry point of a cone hit as sphere tracing detects a delayed hit.
// With sphere tracing, the true cone intersection entry point is somewhere between iterations i-1 and i,
// but cone intersection is only detected at iteration i. This can lead to visual artifacts shading inside a surface.
// CONE_BACKTRACE gives a safe position between iterations i-1 and i, where no hit is guaranteed.
#define CONE_BACKTRACE_T(t, tanT) (t * (1. - tanT / (1. + tanT)))

// when defined, will backtrace and then run normal sphere tracing to determine a hard intersection
// #define CONE_BACKTRACE_SPHERE_TRACE

// maximum number of steps for sphere tracing after applying backtrace correction
#define CONE_BACKTRACE_SPHERE_TRACE_MAX_STEPS 32

/* ---------------------------------------- */

// boundary box parameters
// #define BBOX_FLOOR // toggle for using bbox

#define BBOX_FLOOR_Y -0.25 // y level of floor bbox

/* ---------------------------------------- */
// camera

#define FOCAL_LENGTH 2.5

#define _CAMERA_STATIC false
#define _CAMERA_SPIN 0.025 // angle of each oscillations
#define _CAMERA_SPEED 0.05 // how many oscillations per second

/* ---------------------------------------- */

#define DEBUG_OPACITY 0.99

// #define DEBUG // flag to enable/disable all debugging

// #define DEBUG_NUM_SAMPLES
// #define DEBUG_DEPTH
// #define DEBUG_NORMAL
// #define DEBUG_STEPS

#define _DEBUG_SUBPIXEL_EDGE_RESOLVE_WEIGHT false
#define _DEBUG_SUBPIXEL_EDGE_RESOLVE_COSSIM false
#define _DEBUG_SUBPIXEL_EDGE_RESOLVE_DIRECTION false
#define _DEBUG_SUBPIXEL_EDGE_RESOLVE_CURVATURE false

/* ---------------------------------------- */
// shading

#define USE_SUN
#define USE_SUN_SDF
#define USE_AO
#define USE_SKY_SHADOW
#define USE_FOG

#define USE_CHECKERBOARD

#define FOG_COLOR vec3(0.9, 0.9, 1.2)
#define FOG_FALLOFF 0.000015
#define FOG_START_DISTANCE 2.

#define SKY_COLOR vec3(0.9, 0.9, 1.2)

#define SUN_COLOR vec3(1.00, 0.8, 0.60)
#define SUN_DIR normalize(vec3(-0.5, 0.4, -0.6))

#define CHECKERBOX_INTENSITY 0.85

#define MAX_SHADOW_STEPS 64  // max SDF marching steps for lights
#define MIN_SHADOW_STEP_SIZE 0.01
#define MAX_SHADOW_STEP_SIZE 0.25
#define MIN_SHADOW_TRACE_T 0.001  // minimum ray depth for tracing shadows
#define MAX_SHADOW_TRACE_T 5.  // maximum ray depth for tracing shadows

/* ---------------------------------------- */
// normals

#define NORMAL_IMPL_EPS 0.0001

// 3 different normal calculation implementations
#define NORMAL_IMPL_UNITY 0  // 6 sdf calls, more precise
#define NORMAL_IMPL_UNITY_TETRAHYDRON 1 // 4 sdf calls, less precise
#define NORMAL_IMPL_SHADERTOY 2 // 4 sdf calls, less precise, Shadertoy compile optimization

#define NORMAL_IMPL NORMAL_IMPL_UNITY

/* ---------------------------------------- */
// sdf function

vec2 sdf_showcase(in vec3 p)
{
    vec2 res = vec2(100000, 0);

    p = vec3(p.x, p.y, -p.z);

    res = opU(res, vec2(sdPlane(p, 0.), 0.));

    // bounding box
    if (sdBox(p - vec3(0.0, 0.3, -1.0), vec3(0.35, 0.3, 2.5)) < res.x)
    {
        // more primitives
        res = opU(res, vec2(sdBoundingBox(p - vec3(0.0, 0.25, 0.0), vec3(0.3, 0.25, 0.2), 0.025),
                              16.9));
        res = opU(res, vec2(sdTorus((p - vec3(0.0, 0.30, 1.0)).xzy, vec2(0.25, 0.05)), 25.0));
        res = opU(res, vec2(sdCone(p - vec3(0.0, 0.45, -1.0), vec2(0.6, 0.8), 0.45), 55.0));
        res = opU(res, vec2(sdCappedCone(p - vec3(0.0, 0.25, -2.0), 0.25, 0.25, 0.1), 13.67));
        res = opU(res, vec2(sdSolidAngle(p - vec3(0.0, 0.00, -3.0), vec2(3, 4) / 5.0, 0.4), 49.13));
        res = opU(res, vec2(sdSphere(p - vec3(0, 0.25, -0), 0.15), 26.9));
    }

    // bounding box
    if (sdBox(p - vec3(1.0, 0.3, -1.0), vec3(0.35, 0.3, 2.5)) < res.x)
    {
        // more primitives
        res = opU(res, vec2(sdCappedTorus((p - vec3(1.0, 0.30, 1.0)) * vec3(1, -1, 1),
                                            vec2(0.866025, -0.5), 0.25, 0.05), 8.5));
        res = opU(res, vec2(sdBox(p - vec3(1.0, 0.25, 0.0), vec3(0.3, 0.25, 0.1)), 3.0));
        res = opU(res, vec2(sdCapsule(p - vec3(1.0, 0.00, -1.0), vec3(-0.1, 0.1, -0.1),
                                        vec3(0.2, 0.4, 0.2), 0.1), 31.9));
        res = opU(res, vec2(sdCylinder(p - vec3(1.0, 0.25, -2.0), vec2(0.15, 0.25)), 8.0));
        res = opU(res, vec2(sdHexPrism(p - vec3(1.0, 0.2, -3.0), vec2(0.2, 0.05)), 18.4));
    }

    // bounding box
    if (sdBox(p - vec3(-1.0, 0.35, -1.0), vec3(0.35, 0.35, 2.5)) < res.x)
    {
        // more primitives
        res = opU(res, vec2(sdPyramid(p - vec3(-1.0, -0.6, -3.0), 1.0), 13.56));
        res = opU(res, vec2(sdOctahedron(p - vec3(-1.0, 0.15, -2.0), 0.35), 23.56));
        res = opU(res, vec2(sdTriPrism(p - vec3(-1.0, 0.15, -1.0), vec2(0.3, 0.05)), 43.5));
        res = opU(res, vec2(sdEllipsoid(p - vec3(-1.0, 0.25, 0.0), vec3(0.2, 0.25, 0.05)), 43.17));
        res = opU(res, vec2(sdRhombus((p - vec3(-1.0, 0.34, 1.0)).xzy, 0.15, 0.25, 0.04, 0.08),
                              12.5));
    }

    // bounding box
    if (sdBox(p - vec3(2.0, 0.3, -1.0), vec3(0.35, 0.3, 2.5)) < res.x)
    {
        // more primitives
        res = opU(res, vec2(sdOctogonPrism(p - vec3(2.0, 0.2, -3.0), 0.2, 0.05), 51.8));
        res = opU(res, vec2(sdCylinder(p - vec3(2.0, 0.15, -2.0), vec3(0.1, -0.1, 0.0),
                                         vec3(-0.2, 0.35, 0.1), 0.08), 31.2));
        res = opU(res, vec2(sdCappedCone(p - vec3(2.0, 0.10, -1.0), vec3(0.1, 0.0, 0.0),
                                           vec3(-0.2, 0.40, 0.1), 0.15, 0.05), 46.1));
        res = opU(res, vec2(sdRoundCone(p - vec3(2.0, 0.15, 0.0), vec3(0.1, 0.0, 0.0),
                                          vec3(-0.1, 0.35, 0.1), 0.15, 0.05), 51.7));
        res = opU(res, vec2(sdRoundCone(p - vec3(2.0, 0.20, 1.0), 0.2, 0.1, 0.3), 37.0));
    }

    return res;
}
// A shorter, uglier but faster version of https://www.shadertoy.com/view/XddBD2
vec2 sdf_fast_sponza(in vec3 p)
{
    // y bound
    if (13.75 < p.y)
        return vec2(1., 0.);

    p.y -= 2.0;
    vec3 a0 = p;
    a0.xz = abs(a0.xz) * vec2(-1.0,1.0);
    vec3 a1 = a0 - vec3(6.24,.0,2.5);
    a1.xz = pModPolar(a1.xz, 4.0);
    float d1 = -(a1 - vec3(11.49,.0,.0)).x;
    vec3 a2 = a1 - vec3(11.02,2.15,7.28);
    a2.z = domainRepeat1D(a2.z, 2.0);
    float d3 = fBox(a2 - vec3(-2.64,5.05,.0),vec3(.5,.5,.228));
    d3 = min(d3,fBox(a2 - vec3(-2.275,5.05,.0),vec3(.383,.383,.175)));
    d3 = min(d3,fBox(a2 - vec3(-2.64,6.97,.0),vec3(.5,.283,.111)));
    float d2 = max(-d3,fBox(a2 - vec3(-1.28,6.38,.287),vec3(1.5,1.893,6.673)));
    vec3 a4 = a1 - vec3(9.18,-4.5,-.032);
    a4.y = domainRepeat1D(a4.y, 4.5);
    vec3 a5 = vec3(a4.x, a4.y, domainRepeat1D(a4.z, 2.5));
    vec3 a6 = vec3(-a5.x, a5.y, a5.z);
    vec3 a8 = rdZ(a6 - vec3(.05,-.62,.0));
    float d8 = (fCylinder(a8, 1.398,1.361)*.75);
    d8 = max(-d8,(fCylinder(a8 - vec3(.0,.152,.0), 1.434,.531)*.75));
    float d7 = max(d8,fBox(a6 - vec3(.786,.46,.0),vec3(.523,.747,1.415)));
    float d9 = fBox(a6 - vec3(.47,1.953,.0),vec3(.5,.075,1.5));
    d9 = min(d9,fBox(a6 - vec3(.58,2.2,.0),vec3(.5,.1,1.5)));
    d9 = min(d9,fBox(a6 - vec3(-.45,-2.3,.0),vec3(1.5,.1,1.5)));
    vec3 a10 = a6 - vec3(.463,-.51,1.179);
    a10.z = domainRepeat1D(a10.z, 2.35);
    float d10 = fBox(a10,vec3(.24,.033,.24));
    d10 = min(d10,fBox(a10 - vec3(.0,-.093,.0),vec3(.24,.033,.24)));
    d10 = min(d10,fBox(a10 - vec3(-2.8,-.03,.0),vec3(.25,.075,.25)));
    vec3 a11 = vec3(a10.y, pModPolar(a10.xz , 8.0)).yxz;
    float d11 = fBox(a11 - vec3(.002,-1.07,.0),vec3(.17,1.053,.424));
    vec3 a12 = a6 - vec3(-1.03,-.518,.0);
    vec3 a13 = rdZ(a12);
    float d13 = fCylinder(vec3(a13.x, -a13.z, a13.y), 1.225,3.0);
    d13 = min(d13,fCylinder(a13, 1.094,2.061));
    float d12 = max(-d13,fBox(a12 - vec3(.12,1.27,.0),vec3(1.5,1.355,1.551)));
    vec3 a14 = a6 - vec3(.463,1.57,1.61);
    float d14 = fCylinder(vec3(a14.y, -a14.x, a14.z) - vec3(-.19, -.13, -1.08), .105,.046);
    vec3 polePos = vec3(-a14.y, a14.x, a14.z) - vec3(.042, .596, -1.08);
    polePos.xy += 0.3428 * vec2(polePos.y, -polePos.x);
    d14 = min(d14,fCylinder(polePos, .025,.582));
    float dout = min(min(min(d1,d2),min(min(d7,min(min(d9,min(d10,d11)),d12)),d14)),(a0 - vec3(.0,-2.0,.0)).y);

    return vec2(dout, 4.3);
}

vec2 sdf(in vec3 p)
{
    // for better lighting, remove front wall
    // float zd = p.z - 12;
    // if (0.1 < zd)
        // return zd;

    vec2 prim = sdf_showcase((p - vec3(-0.8, 0.001, 7.25)));
    vec2 sponza = sdf_fast_sponza(p);

    return opU(prim, sponza);
}


/* ---------------------------------------- */
// normals

vec4 calcNormalH4( vec3 pos, const float h ) // for function f(p)
{
    // same as the base implementation below, but also returns the sum of all tested SDF values minus non-zero h0 offset
    vec2 k = vec2(h, 0);
    float h0 = sdf(pos).x;
    float h1 = sdf(pos + k.xyy).x;
    float h2 = sdf(pos - k.xyy).x;
    float h3 = sdf(pos + k.yxy).x;
    float h4 = sdf(pos - k.yxy).x;
    float h5 = sdf(pos + k.yyx).x;
    float h6 = sdf(pos - k.yyx).x;
    return vec4(normalize( vec3(h1 - h2, h3 - h4, h5 - h6)), h1 + h2 + h3 + h4 + h5 + h6 - 6.0 * h0);
}

// https://iquilezles.org/articles/normalsSDF/
#if NORMAL_IMPL==NORMAL_IMPL_UNITY
vec3 calcNormalH( vec3 pos, const float h ) // for function f(p)
{
    vec2 k = vec2(h, 0);
    return normalize( vec3(sdf(pos + k.xyy).x - sdf(pos - k.xyy).x,
                             sdf(pos + k.yxy).x - sdf(pos - k.yxy).x,
                             sdf(pos + k.yyx).x - sdf(pos - k.yyx).x ) );
}
#elif NORMAL_IMPL==NORMAL_IMPL_UNITY_TETRAHYDRON
// assumes that we are computing normal at the surface boundary: sdf(pos) = 0, zero iso-surface of the SDF 
vec3 calcNormalH( vec3 pos, const float h ) // for function f(p)
{
    const vec2 k = vec2(1., -1.);
    return normalize( k.xyy * sdf( pos + k.xyy * h ).x +
                      k.yyx * sdf( pos + k.yyx * h ).x +
                      k.yxy * sdf( pos + k.yxy * h ).x +
                      k.xxx * sdf( pos + k.xxx * h ).x );
}
#elif NORMAL_IMPL==NORMAL_IMPL_SHADERTOY
vec3 calcNormalH( vec3 pos, const float h )
{
    // inspired by tdhooper and klems - a way to prevent the compiler from inlining map() 4 times
    vec3 n = vec3(0., 0., 0.);
    for (int i = 0; i < 4; i++)
    {
        vec3 e = 0.5773 * (2. * vec3((((i + 3) >> 1) & 1), ((i >> 1) & 1), (i & 1)) - 1.);
        n += e * sdf(pos + h * e).x;
        //if( n.x+n.y+n.z>100. ) break;
    }
    return normalize(n);
}
#endif

vec3 calcNormal( vec3 pos )
{
    return calcNormalH(pos, NORMAL_IMPL_EPS);
}

vec3 calcNormal( vec3 pos, const float h )
{
    return calcNormalH(pos, max(NORMAL_IMPL_EPS, h)); // limit minimal value for h
}

/* ---------------------------------------- */
// shading

vec3 normal2color( vec2 normal )
{
    return vec3((normal + 1.) / 2., 0);
}

vec3 normal2color( vec3 normal )
{
    return (normal + 1.) / 2.;
}

// http://iquilezles.org/www/articles/rmshadows/rmshadows.htm
float calcSoftshadow(in vec3 ro, in vec3 rd, in float mint, in float tmax)
{
    float res = 1.;
    float t = mint;
    for (int i = ZERO; i < MAX_SHADOW_STEPS; i++)
    {
        vec3 p = ro + rd * t;
        float h = sdf(p).x;
        float s = clamp(8. * h / t, 0., 1.);
        res = min(res, s * s * (3. - 2. * s));

        #ifdef GRID
        float d2cell = abs(sd2dBox(opRep(p, GRID).xz, 0.5*GRID.xz));
        t += clamp(sign(h.x) * min(max(d2cell, 0.01), abs(h.x)), MIN_SHADOW_STEP_SIZE, MAX_SHADOW_STEP_SIZE); // mod for grid
        #else
        t += clamp(h, MIN_SHADOW_STEP_SIZE, MAX_SHADOW_STEP_SIZE);
        #endif

        if (res < 0.004 || t > tmax) break;
    }
    return clamp(res, 0., 1.);
}

#ifdef USE_AO
float calcAO(in vec3 pos, in vec3 nor)
{
    float occ = 0.;
    float sca = 1.;
    for (int i = ZERO; i < 5; i++)
    {
        float h = 0.01 + 0.12 * float(i) / 4.;
        float d = sdf(pos + h * nor).x;
        occ += (h - d) * sca;
        sca *= 0.95;
        if (occ > 0.35) break;
    }
    return clamp(1. - 3. * occ, 0., 1.) * (0.5 + 0.5 * nor.y);
}
#endif

// http://iquilezles.org/www/articles/checkerfiltering/checkerfiltering.htm
float checkersGradBox(in vec2 p, in vec2 dpdx, in vec2 dpdy)
{
    // filter kernel
    vec2 w = abs(dpdx) + abs(dpdy) + 0.001;
    // analytical integral (box filter)
    vec2 i = 2. * (abs(fract((p - 0.5 * w) * 0.5) - 0.5) - abs(fract((p + 0.5 * w) * 0.5) - 0.5)) / w;
    // xor pattern
    return mix(0.5 - 0.5 * i.x * i.y, 1., 1. - CHECKERBOX_INTENSITY);
    // return 1.;
}

vec3 code2color( const in float m )
{
    return 0.2 + 0.2 * sin(m * 2. + vec3(0., 1., 2.));
}

vec3 shadedColor(
    in vec3 ro, in vec3 rd, in vec3 rdx, in vec3 rdy, in float m, in vec3 pos, in vec3 nor)
{
    // no hit
    if (m < -0.5)
        return vec3(0., 0., 0.);

    vec3 col = code2color(m);
    float ks = clamp(m / 10.0, 0., 1.);

#ifdef USE_CHECKERBOARD
    if (m < 1.5)
    {
        // floor
        nor = vec3(0., 1., 0.);
        // checker grad pattern
        // project pixel footprint into the plane
        vec3 dpdx = ro.y * (rd / rd.y - rdx / rdx.y);
        vec3 dpdy = ro.y * (rd / rd.y - rdy / rdy.y);
        col.x = checkersGradBox(3. * pos.xz, 3. * dpdx.xz, 3. * dpdy.xz);
        col = 0.1 + col * 0.05;
        ks = 1.0;
    }
#endif

#ifdef USE_AO
    // lighting
    float occ = calcAO(pos, nor);
#elif defined (SDF_M_IS_OCCLUSION)
    float occ = m;
#else
    float occ = 0.;
#endif

    vec3 lin = vec3(0., 0., 0.);

#ifdef USE_SUN
    // sun
    {
        vec3 lig = SUN_DIR;
        vec3 hal = normalize(lig - rd);
        float dif = clamp(dot(nor, lig), 0., 1.);
#ifdef USE_SUN_SDF
        if( dif>0.0001 )
            dif *= calcSoftshadow(pos, lig, MIN_SHADOW_TRACE_T, MAX_SHADOW_TRACE_T);
#endif
        float spe = pow(clamp(dot(nor, hal), 0., 1.), 16.);
        spe *= dif;
        spe *= 0.04 + 0.96 * pow(clamp(1. - dot(hal, lig), 0., 1.), 5.);
        lin += col * 2.20 * dif * SUN_COLOR;
        lin += 5.00 * spe * SUN_COLOR * ks;
    }
#endif

#ifdef USE_SKY_SHADOW
    // sky
    {
        vec3 ref = reflect(rd, nor);
        float dif = sqrt(clamp(0.5 + 0.5 * nor.y, 0., 1.));
        dif *= occ;
        float spe = smoothstep(-0.2, 0.2, ref.y);
        spe *= dif;
        spe *= 0.04 + 0.96 * pow(clamp(1. + dot(nor, rd), 0., 1.), 5.);
        //if( spe>0.001 )
        spe *= mix(calcSoftshadow(pos, ref, 0.005, MAX_SHADOW_TRACE_T), 1.0, 0.1);
        lin += col * 0.60 * dif * SKY_COLOR;
        lin += 1.0 * spe * SKY_COLOR * ks;
    }
#endif

    // back
    {
        float dif = clamp(dot(nor, normalize(vec3(0.5, 0., 0.6))), 0., 1.) * clamp(
            1. - pos.y, 0., 1.);
        dif *= occ;
        lin += col * 0.6 * dif * vec3(0.25, 0.25, 0.25);
    }
    // sss
    {
        float dif = pow(clamp(1. + dot(nor, rd), 0., 1.), 2.);
        dif *= occ;
        lin += col * 0.55 * dif * 1.;
    }

    col = lin;

#ifdef USE_FOG
    // fog
    float depth = length(ro - pos);
    float tf = clamp(depth - FOG_START_DISTANCE, 0., depth);
    col = mix(col, FOG_COLOR, 1. - exp(-FOG_FALLOFF * tf * tf * tf));
#endif

    return col;
}

/* ---------------------------------------- */
// rendering

mat3 setCamera(in vec3 ro, in vec3 ta, float cr)
{
    vec3 cw = normalize(ta - ro);
    vec3 cp = vec3(sin(cr), cos(cr), 0.);
    vec3 cu = normalize(cross(cw, cp));
    vec3 cv = (cross(cu, cw));
    return mat3(cu, cv, cw);
}

// returns vec3(ray depth, object id, number of sphere tracing steps)
vec3 sphereTrace(const vec3 ro, const vec3 rd, const float tan_theta, const int max_steps, const float tmin)
{
    float tmax = TMAX;
    #ifdef BBOX_FLOOR
    // raytrace "floor"
    float tp1 = (BBOX_FLOOR_Y - stp.ro.y) / stp.rd.y;
    if (tp1 > 0.)
    {
        tmax = min(stp.t_max, tp1);
    }
    #endif

    // sphere tracing
    float t = tmin;
    for (int i = 0; i < max_steps && t < tmax; i++)
    {
        vec3 p = ro + rd * t; // current pos
        vec2 h = sdf(p); // current sdf

        float coneRad = t * tan_theta;
        float coneOcclusion = (1. - h.x / coneRad) / 2.;

        // classical sphere tracing with cone intersection termination criterion
        if (OCCLUSION_HARD < coneOcclusion)
        {
            return vec3(t, h.y, i); // ray depth and object code
        }

        // increment depth along ray
        float step = h.x * SPHERE_TRACE_STEPSIZE_MULT; // apply step size multiplier
#ifdef GRID_TRACE
        float d2cell = abs(sd2dBox(opRep(p, GRID).xz, 0.5 * GRID.xz));
        t += sign(step) * min(max(d2cell, 0.01), abs(step));
#else
        t += step;
#endif
    }

    return vec3(t, -1., -1);
}

#ifdef NO_CTSS
vec3 render(const vec3 ro, const vec3 rd, const float tan_theta, const vec3 rdx, const vec3 rdy)
{
    // compute intersection with SDFs
    // sample.x = ray depth, sample.y = object id
    vec3 str = sphereTrace(ro, rd, tan_theta, MAX_RAYMARCH_STEPS, TMIN);

    // shade background if sphere tracing does not find intersection
    if (str.z < 0)
    {
#ifdef WHITE_BG
        return vec3(1., 1., 1.);
#else
        return vec3(clamp(FOG_COLOR - max(rd.y, 0.) * 0.3, 0., 1.));
#endif
    }

    // compute shading
    vec3 pos = ro + rd * str.x;
    vec3 nor = calcNormal(pos);
    vec3 color = shadedColor(ro, rd, rdx, rdy, str.y, pos, nor);

#ifdef DEBUG
#ifdef DEBUG_NORMAL
    color = mix(color, normal2color(nor), DEBUG_OPACITY);
#endif

// #define DEBUG_NORMAL_COS
#ifdef DEBUG_NORMAL_COS
    float coneRadius = str.x * tan_theta;
    vec3 norCR = calcNormal(pos, 2.0 * coneRadius);
    float cosSim = dot(nor, norCR);

    if (cosSim < SUBPIXEL_EDGE_RESOLVE_COSSIM_THRESHOLD)
    {
        ;
    } else
        color *= 0;
#endif

#ifdef DEBUG_STEPS
    float steps = float(str.z) / MAX_RAYMARCH_STEPS;
    color = mix(color, steps, DEBUG_OPACITY);
#endif

#ifdef DEBUG_DEPTH
    float depth = t / TMAX;
    color = mix(color, depth * depth, DEBUG_OPACITY);
#endif
#endif

    return color;
}
#else

#define TWO_OVER_FIVE_MINUS_ONE (2. / 5. - 1.)
#define FOUR_OVER_FIVE_MINUS_ONE (4. / 5. - 1.)
#define SIX_OVER_FIVE_MINUS_ONE (6. / 5. - 1.)
#define EIGHT_OVER_FIVE_MINUS_ONE (8. / 5. - 1.)
#define TEN_OVER_FIVE_MINUS_ONE (10. / 5. - 1.)

#define VISMASK_MIN_NORMAL 0.001f

#define SHIFT(x, n) ((0 <= n) ? (x << n): (x >> -n))

struct sample_ctss
{
    vec3 position;
    float t_in;
    float t_out;
    float t_s;
    float id;
    float occlusion;
};

/**
    Returns a 32-bit visibility mask given current cone radius, SDF value, and SDF normal.
    Mask represents ~6x6 subpixel visibility (occupancy).
    Visibility over 6 horizontal lines is encoded with 4, 6, 6, 6, 6, 4 bits (32 bits total).
    Note that corner bits are omitted since 6x6=36, but we only have 32 bits.
    Example bit mask with full visibility (Xs show the space that is omitted in the mask):
        X1111X
        111111
        111111
        111111
        111111
        X1111X
    This will be packed in a single int as 32 bits: 1111 111111 111111 111111 111111 1111
*/
int getVisibilityMask(float coneOcclusion, vec2 nor)
{
    // ensure non-zero normal x, y components
    vec2 n = normalize(vec2(
        clamp(abs(nor.x), VISMASK_MIN_NORMAL, 1.) * (nor.x < 0. ? -1.: 1.),
        clamp(abs(nor.y), VISMASK_MIN_NORMAL, 1.) * (nor.y < 0. ? -1.: 1.)
    ));  // clamp abs then apply sign

    float h = (1. - 2. * coneOcclusion);
    h = -h;

    // define y = ax + b to describe how the surface intersects the traced cone
    // solve for a and b using simple geometry and math
    float a = -n.x / n.y;  // slope;
    float b = h * (n.y * n.y + n.x * n.x) / n.y;  // y intersect

    // NOTE:
    // yIncr = 2. / 5. * r;
    // for i in (0, 1, 2, 3, 4, 5)
    //     py = yIncr * i - r
    //     vis = ((py - b) / a / r + 1.) / 2. = (py - b) / a / r / 2. + 0.5
    float t = 0.5f / a;

    // solve for x (intersection of surface and horizontal line given by y)
    // saturate visibility to 0-1
    float vis0 = clamp((-1.0 - b) * t + 0.5, 0., 1.);
    float vis1 = clamp((TWO_OVER_FIVE_MINUS_ONE - b) * t + 0.5, 0., 1.);
    float vis2 = clamp((FOUR_OVER_FIVE_MINUS_ONE - b) * t + 0.5, 0., 1.);
    float vis3 = clamp((SIX_OVER_FIVE_MINUS_ONE - b) * t + 0.5, 0., 1.);
    float vis4 = clamp((EIGHT_OVER_FIVE_MINUS_ONE - b) * t + 0.5, 0., 1.);
    float vis5 = clamp((TEN_OVER_FIVE_MINUS_ONE - b) * t + 0.5, 0., 1.);

    // apply vis - 1 if n.x is positive
    float vis_r = (0. < n.x) ? -1.: 0.; // 0 or -1
    // convert visibility to bit shift amounts (max 6 bit shift)
    int shift0 = int(6. * (vis0 + vis_r));
    int shift1 = int(6. * (vis1 + vis_r));
    int shift2 = int(6. * (vis2 + vis_r));
    int shift3 = int(6. * (vis3 + vis_r));
    int shift4 = int(6. * (vis4 + vis_r));
    int shift5 = int(6. * (vis5 + vis_r));

    int mask_int = 0;  // 32-bit mask
    int mask4 = 0x1E; // 0b00011110, the side bits are 0s for line 0 and 5
    int mask6 = 0x3F; // 0b00111111;  // all 6 bits are used for lines 1, 2, 3, 4
    mask_int |= (SHIFT(mask6, shift0) & mask4) << 27;  // >> 1 << 28 = << 27; 1 extra right shift (since mask4)
    mask_int |= (SHIFT(mask6, shift1) & mask6) << 22;
    mask_int |= (SHIFT(mask6, shift2) & mask6) << 16;
    mask_int |= (SHIFT(mask6, shift3) & mask6) << 10;
    mask_int |= (SHIFT(mask6, shift4) & mask6) << 4;
    mask_int |= (SHIFT(mask6, shift5) & mask4) >> 1;  // >> 1 << 0 = >> 1; 1 extra right shift (since mask4)

    return mask_int;
}

int bitCountOnes(int num) {
    int count = 0;
    for (int i = 0; i < 32; i++)
    {
        count += num & 1;
        num = num >> 1;
    }
    return count;
}

#ifdef USE_SUBPIXEL_EDGE_RESOLVE
float resolve_primary_edge_visibility(const vec3 n1, const vec3 n2, float h1, float h2,
    const vec3 rd, const vec3 searchDir, float coneRadius)
{
    // note: for all local projections, x axis is along searchDir, y axis is along rd
    // searchDir and rd are orthogonal

    // local projection of the normals of the two planes
    float np1rd = dot(-rd, n1);
    float np2rd = dot(-rd, n2);
    float np1sd = dot(searchDir, n1);
    float np2sd = dot(searchDir, n2);
    // 2d projected normals (without normalization)
    vec2 n1p = vec2(np1sd, np1rd);
    vec2 n2p = vec2(np2sd, np2rd);

    // local points on the two planes
    // h is distance along projected 3d normal
    vec2 p1 = - h1 * n1p; // origin - h1p * n1p
    vec2 p2 = vec2(coneRadius, 0.0) - h2 * n2p;
    // normalize direction only after computing p1 and p2
    n1p = normalize(n1p);
    n2p = normalize(n2p);

    // describe lines as ax - y = -b
    // a = -n.x / n.y because normal is orthogonal to line
    // b = y - a x
    // line 1
    float a1 = -n1p.x / n1p.y;
    float b1 = p1.y - a1 * p1.x;
    // line 2
    float a2 = -n2p.x / n2p.y;
    float b2 = p2.y - a2 * p2.x;

    // determine the intersection point of the two lines
    // we only need the x coordinate
    float xi = (b1 - b2) / (a2 - a1); // local x (along searchDir) position

    // the intersection points controls the visibility of the primary edge sample
    return (clamp(xi, -coneRadius, coneRadius) / coneRadius + 1.0) / 2.0;
}
#endif

vec3 render(const vec3 ro, const vec3 rd, const float tan_theta, const vec3 rdx, const vec3 rdy)
{
    // setup total color and total weight
    vec3 colorTotal = vec3(0., 0., 0.);
    float weightTotal = 0.;

    // record sample information during sphere tracing
#ifndef USE_SUBPIXEL_EDGE_RESOLVE
    sample_ctss samples[CTSS_NUM_SAMPLES];
#else
    sample_ctss samples[CTSS_NUM_SAMPLES + 1]; // +1 sample to allow for edge resolve
#endif

    int numSamples = 0;
    bool hasHardHit = false; // ensure that only the first hard hit is sampled (for each group)

#ifdef USE_CTSS_WEIGHTED
    int visibilityMask = 0;
#endif

    // previous values for hasHit, hardHit, and sdf call
    bool hasHitP = false; // previous
    bool hardHitP = false; // previous
    vec2 hP; // previous ray depth and object code

    bool hasFullHit = false;

    // sphere tracing
    float t = TMIN; // current t
    float tP = t; // initialize previous t as t

    float t_max = TMAX;
#ifdef BBOX_FLOOR
    // raytrace floor plane
    float tp1 = (BBOX_FLOOR_Y - ro.y) / rd.y;
    if (tp1 > 0.)
    {
        t_max = min(t_max, tp1);
    }
#endif

    int numSteps = 0;
    for (; numSteps < _MAX_RAYMARCH_STEPS && t < t_max && numSamples < CTSS_NUM_SAMPLES; numSteps++)
    {
        vec3 p = ro + rd * t;
        vec2 h = sdf(p);

        float coneRad = t * tan_theta;
        float coneOcclusion = (1. - h.x / coneRad) / 2.;

        bool hasHit = OCCLUSION_SOFT < coneOcclusion;
        bool hardHit = OCCLUSION_HARD < coneOcclusion;
        bool fullHit = OCCLUSION_STOP < coneOcclusion;

        bool hitEntry = hasHit && !hasHitP;
        bool hitExit = !hasHit && hasHitP;
        bool hardHitEntry = hardHit && !hardHitP;

        // update previous hit 
        hasHitP = hasHit;
        hardHitP = hardHit;

        if (hasHit)
        {
            if (hitEntry)
            {
                hasHardHit = false;

                // init new sample, record hit group entry point
                // used backtraced intersection: current ray depth minus safe cone radius
                float t_entry = CONE_BACKTRACE_T(t, tan_theta);
                // initialize t in, t out, t sample
                samples[numSamples].t_in = t_entry; 
                samples[numSamples].t_out = t_entry;
                samples[numSamples].t_s = t_entry;
                samples[numSamples].position = ro + rd * t_entry; // sdf
                samples[numSamples].id = h.y; // id
#ifdef USE_CTSS_WEIGHTED
                samples[numSamples].occlusion = coneOcclusion;
#endif
            }

#ifdef USE_CTSS_WEIGHTED
            // update maximal cone occlusion for the current group
            samples[numSamples].occlusion = max(samples[numSamples].occlusion, coneOcclusion);
#endif

            // if hit group has a hard hit, record the first hard hit
            if (hardHitEntry && !hasHardHit)
            {
                hasHardHit = true; // record that a hard hit was found for the current hit group

                // record hard hit intersection
#ifdef CONE_BACKTRACE_SPHERE_TRACE
                // if sphere trace correction is enabled,
                // sphere_trace from (previous ray depth + previous SDF value)
                // TODO: fix this for new ctss_sample struct
                vec3 str_h = sphereTrace(ro, rd, tan_theta, CONE_BACKTRACE_SPHERE_TRACE_MAX_STEPS, tP + hP.x);
                samples[numSamples].xy = str_h.xy; // sdf, id
#else
                // fallback to previous ray depth + previous SDF value (safe position)
                float t_s = tP + hP.x;
                samples[numSamples].t_s = t_s; // sample position
                samples[numSamples].position = ro + rd * t_s; // sdf
                samples[numSamples].id = hP.y; // id
#endif
            }
        }

        // record the sample only when hit group exit is detected or a full hit occurs
        if (hitExit || fullHit)
        {
            // record group exit position
            samples[numSamples].t_out = CONE_BACKTRACE_T(t, tan_theta);

            // reset control vars and increment sample counter
            numSamples++;
        }

        if (fullHit)
        {
            hasFullHit = true;
            break;
        }

        // update previous h and t
        hP = h;
        tP = t;

        float step = h.x * SPHERE_TRACE_STEPSIZE_MULT;

        // increment depth along ray
#ifdef GRID_TRACE
        // if on a grid with randomized objects (for each cell),
        // need to cap maximum move distance to distance from the point within cell to the boundary of the cell,
        // otherwise, the trace may miss geometry and have visual artifacts
        float d2cell = abs(sd2dBox(opRep(p, GRID).xz, 0.5 * GRID.xz));
        t += max(min(max(d2cell, 0.01), abs(step)), CONE_RAD_STEP_MULT * coneRad);
#else
        t += max(step, CONE_RAD_STEP_MULT * coneRad); // speed up sphere tracing near hard hits (sdf ~= 0)
#endif
    }

#if defined(USE_CTSS_WEIGHTED) || defined(USE_SUBPIXEL_EDGE_RESOLVE) 
    // for normal projection, assuming camera is level with the horizon
    // compute right and up vectors given the camera ray rd
    vec3 right = normalize(cross(rd, vec3(0., 1., 0.)));
    vec3 up = normalize(cross(right, rd));
#endif

#ifdef USE_SUBPIXEL_EDGE_RESOLVE
    bool hasSecondaryEdge = false;
    vec3 normalSecondaryEdge = vec3(0.,0.,0.);
    vec2 normal2dResolvedEdge = vec2(0.,0.);

    // read the last sample and apply subpixel edge resolve for full occlusion samples
    sample_ctss s = samples[numSamples - 1];
#ifdef DEBUG
    if (_USE_SUBPIXEL_EDGE_RESOLVE && OCCLUSION_STOP < s.occlusion)
#else
    if (OCCLUSION_STOP < s.occlusion)
#endif
    {
        // calculate small scale normal (the normal of the primary edge)
        float coneRadius = s.t_s * tan_theta;
        // compute small scale normal
        vec3 normalPrimaryEdge = calcNormal(s.position);
        // compute large scale normal (instead of h=epsilon, use h=Rc, where Rc is the current cone radius)
        vec4 normalLarge = calcNormalH4(s.position, _SUBPIXEL_EDGE_RESOLVE_CONE_MULT_COS * coneRadius);

        // compute cosine similarity between small and large scale normals
        float normalSimPrimary = dot(normalPrimaryEdge, normalLarge.xyz);
        float curvature = normalLarge.w;

        // if normals disagree by more than threshold,
        // then pixel contains an edge and we will apply subpixel edge resolve 
        hasSecondaryEdge = normalSimPrimary < _SUBPIXEL_EDGE_RESOLVE_COSSIM_THRESHOLD_MAX &&
            _SUBPIXEL_EDGE_RESOLVE_CURVATURE_THRESHOLD < abs(curvature);

 #ifdef DEBUG
        if (_DEBUG_SUBPIXEL_EDGE_RESOLVE_COSSIM && !_DEBUG_SUBPIXEL_EDGE_RESOLVE_CURVATURE)
        {
            float sim = 1.0 - normalSimPrimary;
            return vec3(sim, sim, sim);
        }

        if (_DEBUG_SUBPIXEL_EDGE_RESOLVE_CURVATURE)
        {
            if (!_DEBUG_SUBPIXEL_EDGE_RESOLVE_COSSIM)
            {
                float curv = (abs(curvature) < _SUBPIXEL_EDGE_RESOLVE_CURVATURE_THRESHOLD) ? 0: sign(curvature);
                curv = (1.0 + curv) / 2.0;
                return vec3(curv, curv, curv);
            }
            else if (!hasSecondaryEdge)
            {
                return vec3(0, 0, 0);
            }
#endif

        if (hasSecondaryEdge)
        {
            // locate the secondary edge side.
            // the sign of the curvature describes local edge as convex/concave,
            // which in turn controls the direction towards the secondary edge.
            // Direction is {from normalPrimaryEdge to normalLarge} for convex,
            // and {from normalLarge to normalPrimaryEdge} for concave edges;
            // curvature is positive for convex and negative for concave edges.
            vec3 searchDir = sign(curvature) * normalize(normalLarge.xyz - normalPrimaryEdge);
            // project the diff direction from world space to camera space
            normal2dResolvedEdge = normalize(vec2(dot(right, searchDir), dot(up, searchDir)));
            // reproject the 2d diff direction back to 3d
            searchDir = normalize(normal2dResolvedEdge.x * right + normal2dResolvedEdge.y * up);

            // resolve the secondary side position
            vec3 resolvedPosition = s.position + searchDir * _SUBPIXEL_EDGE_RESOLVE_CONE_MULT * coneRadius;
            // evaluate normal for secondary side
            normalSecondaryEdge = calcNormal(resolvedPosition);
            // evaluate sdf for secondary side (may be different object id)
            vec2 h2 = sdf(resolvedPosition);
            // offset position by sdf and normal
            resolvedPosition = resolvedPosition - normalSecondaryEdge * h2.x * (1.0 - sign(h2.x) * OCCLUSION_EPS);

#ifdef DEBUG
            if (_DEBUG_SUBPIXEL_EDGE_RESOLVE_CURVATURE)
            {
                if (_DEBUG_SUBPIXEL_EDGE_RESOLVE_COSSIM)
                {
                    float curv = (1.0 + sign(curvature)) / 2.0;
                    return vec3(curv, 1 - curv, 0);
                }
            }

            if (_DEBUG_SUBPIXEL_EDGE_RESOLVE_DIRECTION)
            {
                return normal2color(searchDir);
            }

            // debug mode allows toggle for plane-plane or small-large normals heuristic
            float occlusion;
            if (_SUBPIXEL_EDGE_RESOLVE_TWO_PLANE_INTERSECTION)
            {
                vec2 h1 = sdf(s.position);
                occlusion = resolve_primary_edge_visibility(normalPrimaryEdge, normalSecondaryEdge, h1.x, h2.x, rd,
                    searchDir, _SUBPIXEL_EDGE_RESOLVE_CONE_MULT * coneRadius);
            }
            else
            {
                // compute the half angle between the two normals
                vec3 halfAngle = (normalPrimaryEdge + normalSecondaryEdge) / length(normalPrimaryEdge + normalSecondaryEdge);
                float halfAngleToLargeSim = dot(normalLarge.xyz, halfAngle);
                // estimate visibility of the primary edge using the similarity between large scale and half angle normals 
                occlusion = 1.0 - 0.5 * clamp(remapGlsl(
                    _SUBPIXEL_EDGE_RESOLVE_COSSIM_THRESHOLD_MIN,
                    _SUBPIXEL_EDGE_RESOLVE_COSSIM_THRESHOLD_MAX,
                    0.0,
                    1.0,
                    halfAngleToLargeSim
                    ), 0.0, 1.0);
            }

            if (_DEBUG_SUBPIXEL_EDGE_RESOLVE_WEIGHT)
            {
                return vec3(occlusion, occlusion, occlusion);
            }
#else
            vec2 h1 = sdf(s.position);
            float occlusion = resolve_primary_edge_visibility(normalPrimaryEdge, normalSecondaryEdge, h1.x, h2.x, rd,
                searchDir, _SUBPIXEL_EDGE_RESOLVE_CONE_MULT * coneRadius);
#endif

            // update primary sample visibility
            samples[numSamples - 1].occlusion = occlusion;

            // add secondary sample
            sample_ctss s_r;
            s_r.t_in = s.t_in;
            s_r.t_out = s.t_out;
            s_r.t_s = length(resolvedPosition - ro);
            s_r.position = resolvedPosition;
            s_r.id = h2.y; // this may be different from the primary edge
            s_r.occlusion = 1.0; // full visibility, since secondary side will be sampled after primary side
            samples[numSamples] = s_r;
            numSamples++;
        }
    }
#endif

    // compute shading samples
    for (int i = ZERO; i < numSamples; i++)
    {
        sample_ctss s = samples[i];
        vec3 nor = calcNormal(s.position);
#ifdef USE_SUBPIXEL_EDGE_RESOLVE
        if (hasSecondaryEdge && (i == numSamples - 1)) // only the secondary side (last sample)
            nor = normalSecondaryEdge;
#endif

#ifdef USE_CTSS_WEIGHTED
        // project the normal from world space to camera space
        vec2 nor2d = normalize(vec2(dot(right, nor), dot(up, nor)));
#ifdef USE_SUBPIXEL_EDGE_RESOLVE
        if (hasSecondaryEdge && (i == numSamples - 2)) // only the primary side (second to last sample)
            nor2d = normal2dResolvedEdge;
#endif

        // compute current visibility mask given pixel projected normal and group's maximum cone occlusion
        int visibilityMaskCrt = getVisibilityMask(s.occlusion, nor2d);
        visibilityMaskCrt &= ~visibilityMask; // correlation with previous hits, removes invisible bits
        visibilityMask |= visibilityMaskCrt; // update visibility mask given current visibility, adds visible bits

        float visibility = float(bitCountOnes(visibilityMaskCrt)) / 32.; // visible bit ratio
        float weight = max(MIN_SAMPLE_WEIGHT, visibility);
#else
        float weight = 1.0;
#endif

        vec3 color = shadedColor(ro, rd, rdx, rdy, s.id, s.position, nor);

        colorTotal += weight * color;
        weightTotal += weight;
    }

    // background (sky)
    if (!hasFullHit)
    {
#ifdef USE_CTSS_WEIGHTED
        float bgWeight = 1.0 - float(bitCountOnes(visibilityMask)) / 32.0;
#else
        float bgWeight = 1.0;
#endif

#ifdef WHITE_BG
        colorTotal += bgWeight * vec3(1., 1., 1.);
#else
        colorTotal += bgWeight * vec3(clamp(FOG_COLOR - max(rd.y, 0.) * 0.3, 0., 1.));
#endif
        weightTotal += bgWeight;
    }

    colorTotal /= max(MIN_SAMPLE_WEIGHT, weightTotal);

#ifdef DEBUG
#ifdef DEBUG_STEPS
    float steps = float(numSteps) / MAX_RAYMARCH_STEPS;
    colorTotal = mix(colorTotal, steps, DEBUG_OPACITY);
#endif

#ifdef DEBUG_DEPTH
    float depth = t / TMAX;
    colorTotal = mix(colorTotal, depth * depth, DEBUG_OPACITY);
#endif

#ifdef DEBUG_NUM_SAMPLES
    float v = float(numSamples) / float(CTSS_NUM_SAMPLES + 1);
    colorTotal = mix(colorTotal, v * v, DEBUG_OPACITY);
#endif
#endif

    return colorTotal;
}
#endif

vec3 pixelColor(in vec2 pixel, in vec3 ro, in vec3 ta, in float tan_theta, in float focal_length)
{
    vec3 color = vec3(0., 0., 0.);

    // camera-to-world transformation
    mat3 ca = setCamera(ro, ta, 0.);

    // ray differentials
    vec2 px = (2. * (pixel + vec2(1., 0.)) - iResolution.xy) / iResolution.y;
    vec2 py = (2. * (pixel + vec2(0., 1.)) - iResolution.xy) / iResolution.y;
    vec3 rdx = ca * normalize(vec3(px, focal_length));
    vec3 rdy = ca * normalize(vec3(py, focal_length));

    int aa_factor = int(clamp(_AA_FACTOR, 1, MAX_AA_FACTOR));

    for( int m=ZERO; m<aa_factor; m++ )
    for( int n=ZERO; n<aa_factor; n++ )
    {
        vec2 o = vec2(float(m), float(n)) / float(aa_factor) - (0.5 - 1. / float(2 * aa_factor));
        vec2 p = (2. * (pixel + o) - iResolution.xy) / iResolution.y;

        vec3 fw = normalize(vec3(p, focal_length));

        // ray direction
        vec3 rd = ca * fw;

        color += render(ro, rd, tan_theta, rdx, rdy);

    }
    color /= float(aa_factor*aa_factor);

    // gain
    color = color * 3. / (2.5 + color);

    // gamma
    color = pow(color, vec3(0.4545, 0.4545, 0.4545));

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = 32. + iTime * 0.25;
    float t = (0.5 + 0.5 * sin(2. * 6.28 * _CAMERA_SPEED * time)) * clamp(_CAMERA_SPIN, 0., 1.);
    if (_CAMERA_STATIC)
        t *= 0.0;

    vec2 mo = iMouse.xy / iResolution.xy + vec2(-0.05, 0.15);

    vec3 ta = vec3(-1.8, 0.3, 12.5);
    vec3 ro = ta + 0.1 * vec3(sin(6.28 * (mo.x + t)), -0.1 - 1. * mo.y, cos(6.28 * (mo.x + t)));

    // NOTE: sqrt(2. * dp * dp) = sqrt(2.) * dp; // dp = half pixel size
    // NOTE: (sqrt(2.) * PIXEL_SIZE_MULT) modifies multiplier for pixel diagonal
    float tan_theta = 2. * PIXEL_SIZE_MULT / iResolution.y / FOCAL_LENGTH;

    vec3 color = pixelColor(fragCoord, ro, ta, tan_theta, FOCAL_LENGTH);

    fragColor = vec4(color, 1.);
}

