#define minv -0.55
#define maxv 0.5
#define iters 1000

float map(float value, float min1, float max1, float min2, float max2) {
  return min2 + (value - min1) * (max2 - min2) / (max1 - min1);
}
int calculateSet(vec2 uv) {
    uv += (iMouse.xy / iResolution.xy);
    float a = map(uv.x, 0.0, 1.0, minv, maxv);
    float b = map(uv.y, 0.0, 1.0, minv, maxv);
    float ca = a;
    float cb = b;

    int n = 0;
    while(n < iters) {
        float aa = a * a - b * b;
        float bb = 2.0 * a * b;

        a = aa + ca;
        b = bb + cb;

        if(abs(a + b) > 16.0) break;
        n++;
    }
    return n;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x = uv.x * 2.0 - 1.0;

    int n = calculateSet(uv / pow(iTime,2.0));
    float brt = map(float(n), 0.0, float(iters), 0.0, 1.0);
    if(n == iters) brt = 1.0;
    vec3 col = vec3(brt);

    fragColor = vec4(col,1.0);
}
