vec3 rgb01(vec3 rgbcolor){
    return rgbcolor/256.;
}

//Screen
vec3 screen(vec3 base, vec3 bl_layer){
    return base + bl_layer - base * bl_layer;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec4 tex0 = texture(iChannel0, uv);


    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    //col = vec3(0.5+0.5*abs(cos(iTime+uv.x)), sin(uv.y+iTime), 0.);

    // Output to screen
    fragColor = vec4(col,1.0);

    vec4 color1 = vec4(rgb01(vec3(248.,111.,100.)),1.0);
    vec4 color2 = vec4(rgb01(vec3(253.,93.,146.)),1.0);
    vec4 color3 = vec4(rgb01(vec3(253.,160.,243.)),1.0);

    vec3 colorfinal = color3.rgb;
    colorfinal.r = max(max(color1.r, color2.r), color3.r) - (1.-min(min(color1.r, color2.r), color3.r))*abs(cos(iTime+uv.x*uv.y));
    colorfinal.g = max(max(color1.g, color2.g), color3.g) - (1.-min(min(color1.g, color2.g), color3.g))*abs(cos(iTime/2.+(1.-uv.y*uv.x)+2.));
    colorfinal.b = max(max(color1.b, color2.b), color3.b) - (1.-min(min(color1.b, color2.b), color3.b))*abs(cos(iTime*2.+uv.x*(1.-uv.y)+4.));

    fragColor = mix(tex0,vec4(colorfinal,1.0), 0.2);
    fragColor = vec4(screen(tex0.rgb, colorfinal*0.85),1.0);
    //fragColor = vec4(colorfinal,1.0);

}
