float warp(vec2 uv) {
    float dist = length(uv - iMouse.xy/iResolution.xy);
    return clamp((sin(dist*80.-iTime*3.)/2.+0.5)/pow(dist*40.,2.4),0.,0.1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 samp = texture(iChannel0, uv + warp(uv));

    fragColor = samp;
}
