void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0.0);

    col.r += sin(uv.x*iTime*5.0);
    col.b += sin(uv.y*iTime*5.1);

    col.g += sin(uv.x*uv.y*iTime*5.0);

    col *= vec3(fract(abs(sin(uv.x*iTime*2.0*uv.y))));

    col *= vec3(abs(cos(uv.y*uv.x)));

    fragColor = vec4(col,1.0);
}
