void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    float zs = 50. ; // sin(iTime/5.)*100.;
    vec3 p = vec3(1., uv.x*zs, uv.y*zs);   

    float rs = iTime/20.;
    p.xy *= mat2(cos(rs), -sin(rs), sin(rs), cos(rs));
    rs *= 2.;
    p.xz *= mat2(cos(rs), -sin(rs), sin(rs), cos(rs));

    float col = sin(p.x) * cos(p.y) + sin(p.y) * cos(p.z) + sin(p.z) * cos(p.x);
    // col = (1. - smoothstep(0., .6, col)) * smoothstep(.0, .5, col)*2.3;
    col = smoothstep(1.5, 0., abs(col)/fwidth(col) );
    fragColor = vec4(col,col, col, 1.0);
}
