 float Alpha_Percent = 100.0;
 float Amount = 0.03;
 float Scale = 5.1;
 bool Animate;
 bool Horizontal_Border;
 float Border_Offset = 1.1;
 vec4 Border_Color = vec4(.8,.5,1.0,1.0);

float rand(vec2 co)
{
	float scale = Scale;
//	if (Animate)
//		scale *= rand_f;
	vec2 v1 = vec2(92.0,80.0);
	vec2 v2 = vec2(41.0,62.0);
	return fract(sin(dot(co.xy ,v1)) + cos(dot(co.xy ,v2)) * scale);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    vec4 rgba = texture(iChannel0, uv);
	vec3 tc = rgba.rgb * Border_Color.rgb;

	float uv_compare = uv.x;
	if (Horizontal_Border)
		uv_compare = uv.y;

	if (uv_compare < (Border_Offset - 0.005))
	{
		vec2 randv = vec2(rand(uv.yx),rand(uv.yx));
		tc = texture(iChannel0, uv + (randv*Amount)).rgb;		
	}
	else if (uv_compare >= (Border_Offset + 0.005))
	{
		tc = texture(iChannel0, uv).rgb;
	}
	fragColor = mix(rgba,vec4(tc,1.0),(Alpha_Percent * 0.01));


}
