#define CURVE_X 1.0
#define CURVE_Y 0.5

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Get coords in [-1; 1]
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv *= 2.0;

    // Scale x and y as a function of the complement
    uv.y /= (cos(uv.x * CURVE_X)*0.5+0.5);
    uv.x /= (cos(uv.y * CURVE_Y)*0.5+0.5);

    // Scale back to [0; 1]
    uv /= 2.0;
    uv += 0.5;

    // Sample texture with bent uvs
    vec3 col = texture(iChannel0, uv).rgb;

    // Just to show black borders
    if (uv.x < 0. || uv.x > 1. || uv.y < 0. || uv.y > 1.0) col = vec3(0.);

    fragColor = vec4(col,1.0);
}
