void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0);
    if(uv.y > .5) {
        col = mix(vec3(0,0,0), vec3(.15), uv.x);
    } else {
        float o = float(iFrame%2==0)*2. - 1.;
        col = mix(vec3(0,0,0), vec3(.15) + vec3(.15) * o * 2. * (iMouse.x/iResolution.x), uv.x);
    }

    fragColor = vec4(col,1.0);
}
