float random(float x){
	return fract(sin(1. + x * 12.9898 ) * 43758.5453);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float r = random( floor(uv.x * 1000.));
    float N = uv.y + .1 * sin( iTime + r * acos( -1. ) * 2. ) + .25 * sin( iTime + uv.x * acos( -1. ) * 2. );
    const int T = int( 10. );
    float V[T];
    float S = 0.;
    for( int i = 0; i < T; i++ ){
        V[i] = S;
        S += random( float( i ) + r );
    }    
    int I = 0;
    for( int i = 0; i < T; i++ ){
        V[i] /= S;
        if( V[i] < N ){
            I = i;
        }
    }
    float v = ( I < T - 1 )?V[I+1 ]:1.;
    fragColor = vec4((N-V[I])/(v-V[I]) );// or just V[I] :)
}
