#define sat(a) clamp(a, 0., 1.)
float time;

float _sqr(vec2 uv, vec2 s)
{
    vec2 l = abs(uv)-s;
    return max(l.x, l.y);
}

float _shape(vec2 uv, float s, float id)
{
    return mix(_sqr(uv, vec2(s)), length(uv)-s, 5.*sin(time+id)); 
}

vec3 rdr(vec2 uv)
{
    float sharp = iResolution.x*.75;
    vec3 col = vec3(0.);

    vec2 rep = vec2(.005);
    vec2 id = floor((uv+rep*.5)/rep);

    uv = mod(uv+rep*.5,rep)-rep*.5;

    float shape = _shape(uv, .001, id.x*.1+id.y*sin(time+id.x*.01)*.5);
    if (abs(id.x) < 50. && abs(id.y) < 50.)
        col = mix(col, vec3(1.), 1.-sat(shape*sharp));

    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    time = iTime;
    float off = sin(iTime)*.2;
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.xx;

    vec3 col = vec3(0.);
    col.x = rdr(uv).x;
    time = iTime+off;
    col.y = rdr(uv).y;
    time = iTime+off*2.;
    col.z = rdr(uv).z;
    fragColor = vec4(col,1.0);
}
