void mainImage( out vec4 fragColor, in vec2 u )
{
    vec4 a = texelFetch(iChannel0,ivec2(u),0);
    uint b = floatBitsToUint(a.x)&255U;
    int i = iFrame&7;
    b = (b>>i)|(b<<(8-i));
    fragColor = cos(float(b&255U)*77.1111+vec4(3,1,2,4))*.5+.5;
}

uint read(vec2 u, vec2 m)
{
    vec2 o = mod(u+m,iResolution.xy);
    return floatBitsToUint(texelFetch(iChannel0,ivec2(o),0).x);
}
void mainImage( out vec4 fragColor, in vec2 u )
{
    int  tz  = 7;//layers across time
    int  sz  = 1;//3x3 pixel grids
    uint sum = 0U;
    for(int t= 0 ; t< tz; ++t){
    for(int i=-sz; i<=sz; ++i){
    for(int j=-sz; j<=sz; ++j){ sum += (read(u,vec2(i,j))>>t)&1U;
    }}}
    uint a = +16U*8U*16U*8U* ((3U<<10U)|(1U<<3U))
             +16U*8U*16U*    2U
             +16U*8U*        0U
             +16U*           1U
             +               15U;
    if(sum>=32U){a = ((1U<<13U)-1U)<<3U;}
    uint o = read(u,vec2(0));
         o = (o<<1U) | ((a>>(sum&31U))&1U);

    if(iFrame==0||iMouse.z>.5)
    {
        vec2  v = (u*2.-iResolution.xy)/iResolution.y;
        float r = fract(cos(dot(u,vec2(11.76543,iTime+22.5363)))*3467.5678)*256.;
              r*= step(dot(v,v),.7);
              r*= step(.6,dot(v,v));
        o = uint(r);
    }
    fragColor = vec4(uintBitsToFloat(o));
}
