vec3 BLUE = vec3(0,.34,.72);
vec3 YELLOW = vec3(1,.84,0);
vec3 BLACK = vec3(0,0,0);

vec3 Flag(vec2 uv){
    float w = fwidth(uv.y);

    float y = smoothstep(0.5 - w, 0.5 + w, uv.y);

    vec3 col = mix(YELLOW,BLUE, y);

    float mB = smoothstep(w, .0, abs(uv.y-.5)-.5+w);
    col = mix(BLACK,col, mB);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float xWave = 10.0;
    float yWave = 7.0;


    float t = uv.x * xWave + uv.y * yWave - 2.0 * iTime;
    uv.y += sin(t) * 0.03;

    vec3 col = Flag(uv);

    col *= 1.+cos(t)*0.3;

    fragColor = vec4(col, 1.0);
}
