// https://www.desmos.com/calculator/npvnj3cbj9
float plucked_string ( float freq, float shift, float t, float x ) {
    float height = 0.5 * (  ( 1./(1.+pow(freq*( x - shift ) + t, 2.)) ) + ( 1./(1.+pow(freq*( x - shift ) - t, 2.)) )  );
    return height;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float t = iTime;

    float wave_sharpness = 5.;

    float time_of_boundary_contact = wave_sharpness * 0.5;

    vec3 col = vec3( plucked_string( wave_sharpness, 0.5, time_of_boundary_contact * sin(t), uv.x ) );



    // 
    col = col * vec3(0.0, 1., 0.7);

    // Output to screen
    fragColor = vec4(col,1.0);
}
