vec3 sampleColor(vec3[9]colors, vec2 id)
{
    //id = max(vec2(0.0), min(vec2(2.0), id));
    //int id1d = int(id.y * 3.0 + id.x);
    //vec3 col = colors[id1d];
    //return col;

    ivec2 iid = clamp(ivec2(id),0, 2);
    return colors[iid.y*3 + iid.x];

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;
    uv.y = 1.0 - uv.y;
    uv *= 5.0;
    uv -= 1.0;

    vec2 pos = fract(uv);

    vec2 id = floor(uv);

    vec3 colors[] = vec3[9](vec3(1, 0, 0), vec3(0, 1, 0), vec3(0, 0, 1), 
                            vec3(1, 1, 0), vec3(1, 0.5, 0.2), vec3(1, 0, 1), 
                            vec3(0, 1, 1), vec3(0.5, 0, 0.2), vec3(0.5, 0, 1.0));


    vec3 col = sampleColor(colors, id);

    vec2 bias = vec2(0.5, -0.5);

    vec2 id00 = floor((uv + bias.yy));
    vec2 id01 = floor((uv + bias.yx));
    vec2 id10 = floor((uv + bias.xy));
    vec2 id11 = floor((uv + bias.xx));

    vec3 col00 = sampleColor(colors, id00);
    vec3 col01 = sampleColor(colors, id01);
    vec3 col10 = sampleColor(colors, id10);
    vec3 col11 = sampleColor(colors, id11);

    vec2 center00 = floor(uv + bias.yy) + 0.5;
    vec2 samplePos = uv - center00;

    vec3 col0 = mix(col00, col01, samplePos.y);
    vec3 col1 = mix(col10, col11, samplePos.y);

    col = mix(col0, col1, samplePos.x);

    if(id.x < 0.0 || id.y < 0.0 || id.x > 2.0 || id.y > 2.0)
        col = vec3(0.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}
