float squares (float x, float y, float rep)
{
    if ((int(floor(x/rep))&1) == (int(floor(y/rep))&1))
        return 1.0;
    else
        return 0.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float f = fragCoord.x/1000.0+fragCoord.y/2000.0+1.0+iTime/10.0;

    mat2 m = mat2 (cos(f), sin(f), -sin(f), cos(f));

    vec2 k = (fragCoord.xy-iResolution.xy/2.0)*m;

	vec3 pixel;

    pixel.r = squares(k.x, k.y+iTime*200.0, 96.0);
    pixel.g = squares(k.x+iTime*100.0, k.y, 112.0);
    pixel.b = squares(k.x+iTime*50.0, k.y+iTime*50.0, 200.0);

	fragColor = vec4(pixel, 1.0);
}
