#ifdef GL_ES
precision mediump float;
#endif

#define PI 3.14159265359
#define TWO_PI 6.28318530718

//  Function from IÃ±igo Quiles
//  https://www.shadertoy.com/view/MsS3Wc
vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix( vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 color = vec3(0.0);
    float speed = 1.5;
    float minRadius = .2;
    float maxRadius = .5;
    float radius = minRadius + (maxRadius - minRadius) * (.5+sin(iTime)/2.0);

    // Fix aspect ratio
    float ratio = iResolution.x / iResolution.y;
    vec2 pixel = fragCoord.xy / iResolution.xy;
    pixel.x *= ratio;    

    // Use polar coordinates instead of cartesian
    vec2 center = vec2(.5*ratio,.5);
    vec2 toCenter = center-pixel;

    float angle = atan(toCenter.x,toCenter.y);

    if (length(toCenter) <= radius) {
        // Map the angle (-PI to PI) to the Hue (from 0 to 1)
        // and the Saturation to the radius
        float hue = angle/TWO_PI - iTime*speed;
        float saturation = length(toCenter) / radius;
        float brightness = 1.0;

        color = hsb2rgb(vec3(hue, saturation, brightness));
    }

    fragColor = vec4(color,1.0);
}
