#define SPEED 2.
#define RANGE .5
#define Strength .5

vec2 inflate(vec2 uv, vec2 center, float radius, float strength) {
    float dist = distance(uv , center);
    vec2 dir = normalize(uv - center);
    float scale = 1.-strength + strength *smoothstep(0.,1.,dist/radius);
    float newDist = dist * scale;
    return center + newDist * dir;
}
void mainImage(out vec4 fragColor, vec2 coord) {
    vec2 uv = coord / iResolution.xy;
    // vec2 pos = iMouse.xy / iResolution.xy;
    vec2 pos = vec2(.5);
    uv = inflate(uv, pos, RANGE, Strength * sin(iTime * SPEED));
    vec3 color = texture(iChannel0, uv).rgb;
    fragColor.rgb = color;
}
