    float Circle(vec2 uv, vec2 position, float r, float blur) 
    {
        float d = length(uv - position);
        float c = smoothstep(r, r-blur, d);
        return c;
    }

void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
    vec2 uv = fragCoord.xy / iResolution.xy;    
    uv -= .5;
    uv.x *= (iResolution.x / iResolution.y); 

    // голова
    float c = Circle(uv, vec2(.0, .25), .2, .005);

    // глаза
    c -= Circle(uv, vec2(.1, .3), .05, .005);    
    c -= Circle(uv, vec2(-.1, .3), .05, .005);
    c -= Circle(uv, vec2(.1, .28), .025, .005);    
    c -= Circle(uv, vec2(-.1, .28), .025, .005);
    c += Circle(uv, vec2(.1, .3), .045, .001);       
    c += Circle(uv, vec2(-.1, .3), .045, .001); 

    //нос
    c -= Circle(uv, vec2(-.0009, .24), .05, .005);
    c += Circle(uv, vec2(-.04, .21), .02, .005);
    c += Circle(uv, vec2(.04, .21), .02, .005);

    // тело
    c += Circle(uv, vec2(.0, -.18), .25, .005);

    // уши
    c += Circle(uv, vec2(.3, .25), .15, .005);
    c += Circle(uv, vec2(-.3, .25), .15, .005);
    c -= Circle(uv, vec2(.3, .25), .14, .005);
    c -= Circle(uv, vec2(-.3, .25), .14, .005);
    c += Circle(uv, vec2(.3, .25), .13, .005);
    c += Circle(uv, vec2(-.3, .25), .13, .005);

    //руки
    c += Circle(uv, vec2(.3, -.1), .1, .005);
    c -= Circle(uv, vec2(.32, -.04), .03, .005);
    c -= Circle(uv, vec2(.36, -.09), .03, .005);
    c -= Circle(uv, vec2(.34, -.15), .03, .005);
    c -= Circle(uv, vec2(.28, -.101), .045, .005);
    c += Circle(uv, vec2(-.3, -.1), .1, .005);
    c -= Circle(uv, vec2(-.32, -.04), .03, .005);
    c -= Circle(uv, vec2(-.36, -.09), .03, .005);
    c -= Circle(uv, vec2(-.34, -.15), .03, .005);
    c -= Circle(uv, vec2(-.28, -.101), .045, .005);

    //ноги
    c += Circle(uv, vec2(.18, -.4), .1, .005);
    c += Circle(uv, vec2(-.18, -.4), .1, .005);

    fragColor = vec4(vec3(c), 1.0);
}
