    float Circle (float x, float x1, float y, float y1, float r) {
        return -1./((x-x1)*(x-x1)+(y-y1)*(y-y1)-r);
    }


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = 1.0 - 2.0* fragCoord/iResolution.xy;
     float x = p.x;
     float y = p.y*iResolution.y/iResolution.x;
     float z = Circle(x, 0., p.y / 3., 0., 0.02); // body
         z = max(z, Circle(x, 0., y, -0.3, 0.02)); // head
         z *= Circle(x, 0.05, y, -0.32, 0.0013); //right eye
         z *= Circle(x, -0.05, y, -0.32, 0.0013); //left eye
         z *= Circle(x, 0., y, -0.25, 0.0018); // red
         z = min(z, -Circle(x, 0., y, -0.26, 0.0015)); // mouth
         z -= Circle(x, 0.27, y, -0.32, 0.027); //right ear
         z -= Circle(x, -0.27, y, -0.32, 0.027); //left ear
         z -= Circle(x, 0.1, p.y, 0.4, 0.008); // rightfoot 
         z -= Circle(x, -0.1, p.y, 0.4, 0.008); // leht foot
         z -= Circle(x, 0.15, p.y/3.2, -0.001, 0.006); // right arm
         z -= Circle(x, -0.15, p.y/3.2, -0.001, 0.006); // left arm
           vec3 col;
              if(z>=0.) col.g = z;
                else col.r = -z;
                  fragColor = vec4(col,1.0);

}
