precision highp float;

const float PI = 3.14;
const float e = 2.71;
const float n = sqrt(2.);

const float R0 = 0.5 * e/PI;

const vec4 background = vec4(1., 1., 1., 1.);

const vec4 color = 1. - vec4(10., 102., 194., 255.)/255.;

vec2 rotZ(vec2 point, float angle) {
    float c = cos(angle);
    float s = sin(angle);
    vec2 finalPoint = mat2(c, -s, s, c) * point;
    return finalPoint;
}

vec3 drawCircle(vec2 uv, float R, float angle) {
    uv = rotZ(uv, angle);
    float yy = min(exp(-(n * PI * uv.x/R - sin((PI/R) * uv.y) * cos(iTime + PI))/0.03), 1.);
    float mask = smoothstep(R + 0.01, R, length(uv));
    float r = exp((-(abs(length(uv) - R)) + 0.01)/0.02);
    return vec3(yy * mask + r);
}

vec4 draw(vec2 uv, float R, float angle, bool enableGradX) {
    float r = length(uv);

    vec2 pointC = vec2(R * cos(angle), R * sin(angle));
    vec2 pointA = vec2(-R, 0.);
    vec2 pointB = vec2(R, 0.);

    vec3 c = drawCircle(uv, R, angle);
    vec3 sq = background.rgb;

    if (enableGradX) {
        sq = vec3(smoothstep(0., 2., -uv.x * -sin(angle + PI/2.)));
    }

    vec3 sn = vec3(exp(-abs(distance(uv, pointC)) * 10.));

    vec2 uv1 = rotZ(vec2(uv.x + pointA.x, uv.y), angle/2.);
    vec3 l1 = vec3(exp(-abs(uv1.x) * 100.));
    vec2 uv2 = rotZ(vec2(uv.x + pointB.x, uv.y), (angle - PI)/2.);
    vec3 l2 = vec3(exp(-abs(uv2.x) * 100.));
    vec3 l3 = vec3(exp(-abs(uv.y) * 100.));

    return vec4(c + sq + sn + l1 + l2 + l3, 1.) * color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( 2. * fragCoord - iResolution.xy ) / iResolution.y;

    float angle = iTime * e/PI + PI/2.;
    fragColor = draw(uv, R0, angle, true);
}
