float sdRoundedBox( in vec2 p, in vec2 b, in vec4 r )
{
    r.xy = (p.x>0.0)?r.xy : r.zw;
    r.x  = (p.y>0.0)?r.x  : r.y;
    vec2 q = abs(p)-b+r.x;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r.x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float opacity = 0.6;
    float xMargin = 100.0;
    float yMargin = 70.0;
    float cornerRadius = 100.0;
    vec4 texC = texture(iChannel0, fragCoord/iResolution.xy);
    float color = sdRoundedBox(2.0 * fragCoord - iResolution.xy, vec2(iResolution.x - xMargin * 2.0, iResolution.y - yMargin * 2.0), vec4(cornerRadius));
    fragColor = ((1.0 - opacity) * step(1.0, sign(color)) + opacity) * texC;
}
