void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;      

                       /*须知：iMouse是4维变量 = 鼠标x坐标 +鼠标y坐标 +鼠标左键点击事件
                               鼠标x，y坐标范围是屏幕坐标，需要归一化
                               把iResolution 改成 iMouse即可
                               需要通过点击鼠标左键来实现鼠标交互               
                       */
    vec2 mo = fragCoord/iMouse.xy;


    fragColor =  vec4(uv.x  ,abs(sin(iTime))  ,mo.x  ,1.0);
}
