// You can use this shader as a template for ray marching shaders

#define MAX_STEPS 1000
#define MAX_DIST 100.
#define SURF_DIST .0001
//#define BBOX_SIZE vec3(50.,2.5,50.)
#define BBOX_SIZE vec3(5.,1.5,5.)

#define LIGHT_MARCH_STEP_SIZE 0.1
#define LIGHT_MARCH_SAMPLING_STEP_SIZE 0.2
#define VOLUME_MARCH_STEP_SIZE 0.01
#define TRANSPARENCY_STOP_THRESHOLD 0.01

#define WHITE_BACKGROUND 0
#define NO_LIGHT 0
#define TEST_ON 1
#define USE_ALBEDO 0

#if USE_ALBEDO == 1  //  Either set the albedo value (which corresponds to the material color)
const vec3 albedo = vec3(0.,0.5,0.5);  //Portion of light returning to the observer
const vec3 sigma_t = vec3(0.0, 0.5, 0.5);  //Portion of light that keeps going after traversing the volume
const vec3 sigma_s = albedo*(1.-sigma_t);
const vec3 sigma_a = (1.-albedo)*(1.-sigma_t);

#else  // Or set the sigma_t and sigma_a values
// sigma_s + sigma_a +  sigma_t MUST be equal to vec3(1.)
const vec3 sigma_s = vec3(.5,.5,.5);  //Portion of light scattered back when traversing the volume
const vec3 sigma_a = vec3(.1);  //Portion of light absorbed by the material
const vec3 sigma_t = 1.-sigma_a-sigma_s;  //Portion of light that keeps going after traversing the volume

#endif

float unit_density = 2.;
//vec3 lightColor = vec3(1.); // White light
vec3 lightColor = vec3(0.98, 0.92, 0.84); //Sun light
#define NOISE_THRESHOLD 0.1
#define NOISE_OCTAVE 5
#define SMOOTHSTEP_OCCUPANCY 1
#define NOISE_SMOOTHSTEP_UP_THRESHOLD 1.



#define hash33(p) fract(sin((p)*mat3(127.1,311.7, 74.7,  269.5,183.3,246.1,  113.5,271.9,124.6))*43758.5453123)

vec3 rand_unit_vector(vec3 p)
{
    return normalize(2.*hash33(p)-1.);
}

float perlin_noise(vec3 p,float grid_size)
{
    p*=grid_size;
	vec3 pi = floor(p);
    vec3 pf = p-pi;
    vec3 w = pf*pf*(3.-2.*pf);

    float f000 = dot(rand_unit_vector(pi+vec3(.0,.0,0.)),pf-vec3(.0,.0,0.));
    float f001 = dot(rand_unit_vector(pi+vec3(.0,.0,1.)),pf-vec3(.0,.0,1.));
    float f010 = dot(rand_unit_vector(pi+vec3(.0,1.0,0.)),pf-vec3(.0,1.0,0.));
    float f011 = dot(rand_unit_vector(pi+vec3(.0,1.0,1.)),pf-vec3(.0,1.0,1.));
    float f100 = dot(rand_unit_vector(pi+vec3(1.0,.0,0.)),pf-vec3(1.0,.0,0.));
    float f101 = dot(rand_unit_vector(pi+vec3(1.0,.0,1.)),pf-vec3(1.0,.0,1.));
    float f110 = dot(rand_unit_vector(pi+vec3(1.0,1.0,0.)),pf-vec3(1.0,1.0,0.));
    float f111 = dot(rand_unit_vector(pi+vec3(1.0,1.0,1.)),pf-vec3(1.0,1.0,1.));

    float xm1 = mix(f000,f100,w.x);
    float xm2 = mix(f001,f101,w.x);
    float xm3 = mix(f010,f110,w.x);
    float xm4 = mix(f011,f111,w.x);
    float ym1 = mix(xm1,xm2,w.z); 
    float ym2 = mix(xm3,xm4,w.z); 
    float zm = mix(ym1,ym2,w.y);

    return zm*1./sqrt(1./2.);
}

float noise_sum(vec3 p,int octave){
	float a = 1., r = 0., s=0.;

    vec3 o[NOISE_OCTAVE] ;
    o[0] = p;
    for (int i=1; i<NOISE_OCTAVE; i++){
        o[i] = o[i-1]*2.;
    }

    for (float i=0.; i<float(octave); i+=1.){
        r += a*perlin_noise(o[int(i)],1.);
        s = s + a;        
        a = a*0.5;
    }
    return r/s;
}

float noise_mul(vec3 p,int octave){
	float r = 1., s=1.;
    vec3 o[NOISE_OCTAVE] ;
    o[0] = p;
    for (int i=1; i<NOISE_OCTAVE; i++){
        o[i] = o[i-1]*2.;
    }
    for (float i=0.; i<float(octave); i+=1.){
        r *= smoothstep(-0.5,0.5, perlin_noise(o[int(i)],1.))*2.;
        s *= 2.;        
    }
    return r/s;
}


mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float sdSphere(vec3 p, vec3 center, float radius){
    return distance(p,center)-radius;
}


float GetDist(vec3 p) {
    float d = sdBox(p, BBOX_SIZE);


    return d;
}


vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}



float RayMarchInside(vec3 ro, vec3 rd) {
	float dO=0.;
    float last_dS = 0.;
    float lastdS = 0.;
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += abs(dS);

        if (dO>MAX_DIST || abs(dS)<SURF_DIST*10.0 && last_dS<dS){
            break;
        }
        last_dS = dS;
    }

    return dO;
}


vec2 RayMarchVolume(vec3 ro, vec3 rd) {
    if (GetDist(ro) < 0.){
        return vec2(0.,RayMarchInside(ro,rd) );
    }

	float dO=0.;

    bool is_inside = false;
    float in_dist = 0.;
    float out_dist = 0.;
    float last_dS = 0.;
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += abs(dS);

        if (dO>MAX_DIST){
            break;
        }
        if (abs(dS)<SURF_DIST){
            if (is_inside == false){
                in_dist = dO;
                is_inside = true;
            } else if (last_dS<dS){
                out_dist = dO;
                return vec2(in_dist,out_dist);
            }
            dO+= SURF_DIST;//SURF_DIST*2.-dS;
        }
        last_dS = dS;
    }
    return vec2(dO);
}



float occupancyFunction(vec3 p){
    float noise = noise_sum(p,NOISE_OCTAVE);
    float threshold = NOISE_THRESHOLD;
    #if SMOOTHSTEP_OCCUPANCY == 0
        float clouds = step(threshold,noise);
    #else
        float clouds = smoothstep(threshold, NOISE_SMOOTHSTEP_UP_THRESHOLD,noise);
    #endif
    float objects = min (1., clouds);//
    return objects;
}


float GetLocalDensity(vec3 p, float len){
    float occupancy = occupancyFunction(p);
    return occupancy*unit_density;
}

vec3 calculateTransparency(float len, float density){
    return exp(-len *density * (1.-sigma_t) );
}


vec4 GetVolumeColor(vec3 ro, vec3 rd, float dist_start, float dist_end,vec3 lightDir, vec3 background){
    if (dist_end-dist_start == 0.){
        return vec4(0.);
    }

    vec3 color = vec3(0.); //accumulated color as we move into the volume
    vec3 T_acc = vec3(1.); //accumulated transparency as we move into the volume
    vec3 localTransparency;

    float total_dist = dist_end-dist_start;
    float step_size = VOLUME_MARCH_STEP_SIZE;

    float localDensity;
    vec3 p;

    //ro -= step_size * rd * hash33(gl_FragCoord.xyy).x;

    vec3 albedo = sigma_s / (sigma_s + sigma_a);

    for (float dist=0.; dist<total_dist; dist+=step_size){
        p = ro + rd*(dist_start+dist); //p += rd*dist;
        localDensity = GetLocalDensity(p, step_size);
        if (localDensity>0.){

            localTransparency = calculateTransparency(step_size, localDensity);
            vec3 localOpacity = 1.-localTransparency;
            vec3 phase = vec3(1.);  //Fraction of light reflected depending on the angle to the light, per color. 1. for now
            vec3 illum = lightColor * phase; //Should actually compute how much light reaches through the volume

            vec3 lightReachingPoint = illum * phase * T_acc;
            //vec3 lightReachingPoint = illum * phase ;
            vec3 materialLocalColor = albedo * localOpacity;
            color+= T_acc * materialLocalColor * lightReachingPoint ;

            T_acc*=localTransparency; //The probability to have light through multiple slices is the multiplication of the probability of the slices

            if (T_acc.x + T_acc.y + T_acc.z < TRANSPARENCY_STOP_THRESHOLD){
                //st_assert(false);
                return vec4(color,T_acc) ;//break;
            }



        }

        step_size = VOLUME_MARCH_STEP_SIZE*max(0.05,sqrt(dist)); //max to avoid infinite loop when dist = 0


    }

    color = color + T_acc * background;
    return vec4(color,T_acc) ;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.*(fragCoord.xy-(0.5*iResolution.xy))/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;
    #if NO_LIGHT
        lightColor *= vec3(uv.y<0.);
    #endif

    vec3 ro = vec3(0, 3, -3);
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);

    vec3 rd = GetRayDir(uv, ro, vec3(0,0.,0), 1.);
    vec3 background = vec3(0.);
    #if WHITE_BACKGROUND
        background = vec3(uv.x>0.);
    #endif
    vec3 col = background;

    vec2 distances = RayMarchVolume(ro, rd);
    float d = distances.x;

    //vec3 lightPos = vec3(3.,1.,5.);
    vec3 lightPos = vec3(sin(iTime),1.,cos(iTime));

    //vec3 lightPos = vec3(2.,1.,0.);

    if(d<MAX_DIST) {
        vec3 lightDir = normalize(lightPos);
        vec4 volumeColor = GetVolumeColor(ro,rd,distances.x,distances.y, lightDir, col);

        col = volumeColor.xyz;
    }


    col = pow(col,vec3(1./2.2));
    fragColor = vec4( col ,1.0);

    #if TEST_ON == 1
        st_assert( length((sigma_s + sigma_a + sigma_t) - 1.) < 1e-5);

    #endif

}
