void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalize UV (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;

    // animation
    float delta = sin(iTime * 0.75) * 0.5;

    // define circle field
    // 1
    vec2 circle1Center = vec2(0.78,0.5);
    float d1 = distance(uv,circle1Center);
    float f1 = 1. / (pow(d1, 2.) * 100.);
    // 2
    vec2 circle2Center = vec2(0.78 + delta,0.5);
    float d2 = distance(uv,circle2Center);
    float f2 = 1. / (pow(d2, 2.) * 100.);

    // clamp & smooth
    float clampped = (clamp(f1 + f2, 0.7, 1.) - 0.7) / (1. - 0.7);
    clampped = clampped > 0.1 ? 1. : (clampped * 10.);

    // Output to screen
    fragColor = vec4(vec3(0.27, 0.6, 1.) * clampped, 1.0);
}
