void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //found at https://www.shadertoy.com/view/Mds3zn
    //setting up chromatic aberration
    float amount = 0.0;

	amount = (1.0 + sin(iTime*6.0)) * 0.5;
	amount *= 1.0 + sin(iTime*16.0) * 0.5;
	amount *= 1.0 + sin(iTime*19.0) * 0.5;
	amount *= 1.0 + sin(iTime*27.0) * 0.5;
	amount = pow(amount, 3.0);

    amount *= 0.05;

    //scroll uv
    uv -= vec2(iTime * 0.3,0.0);

    //wave
    uv.y += sin(uv.x * 1.5 + iTime * 3.0) * 0.1;

    //setting up texture
    vec4 col = texture(iChannel0, uv);
    vec4 col2 = texture(iChannel1, uv);

    //mixing and displaying cat
    float t = 1.0 - step(col2.a, 0.6);


    //finishing chromatic aberration
    col.r = texture( iChannel0, vec2(uv.x+amount,uv.y) ).r;
    col.g = texture( iChannel0, uv ).g;
    col.b = texture( iChannel0, vec2(uv.x-amount,uv.y) ).b;

	col *= (1.0 - amount * 0.5);

    //adding cat
    col = mix(col,col2, t);

    //messing with colors
    col*=vec4(1.0,sin(iTime * 2.0) * 5.0,1.0, 1.0);

    // Output to screen
    fragColor = col;
}
