void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy) - 0.5;
    uv.x *= iResolution.x / iResolution.y;


    uv = fract(uv * 2.);

    float d = length(uv.xy - 0.5 );

    float c = smoothstep(0.1, 0.44 + abs(sin(iTime)) -0.2, d);
    c= fract(c * 45.);

    float b = smoothstep(0.01, 0.009, uv.x);
    float a = smoothstep(0.01, 0.009, uv.y);

    fragColor = vec4(c + b + a);
}
