void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy;
    xy.x = xy.x / iResolution.x;
    xy.y = xy.y / iResolution.y;

    vec4 color = vec4(0.0, 0.0, 0.0, 0.0);
    if(xy.x > 0.5) {
        color.r = 1.0;
    }

    // Output to screen
    fragColor = color;
}
