#define s(x) smoothstep(0.15, 0.3, x * 1.1 - 0.1)

vec3 chromaKey(vec3 x, vec3 y){
	vec2 c = s(vec2(x.g - x.r * x.y, x.g));

    return mix(x, y, c.x * c.y);
}

vec2 rotate2D(vec2 v, float angle)
{
    float c = cos(angle);
    float s = sin(angle);

    return vec2(c*v.x-s*v.y, s*v.x+c*v.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float theta = iTime/4.0f;
    uv = rotate2D(uv, theta);
    // Zoom:
    uv *= 1.5f*(cos(iTime) + 1.2f);
    // Pan:
    uv += vec2(3.0f*cos(iTime/2.0f), 2.0f*sin(iTime/2.0f));

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime*6.0f+uv.xyx+vec3(0,2,4));

    // Read a video with green screen background
    vec4 vid = texture(iChannel0, uv);
    vec3 pixelColour = chromaKey(vid.rgb, col.rgb);

    fragColor = vec4(pixelColour, 1.0f);
}
