void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
    // UV manipulation
    vec2 uv = fragCoord.xy - 0.5 * iResolution.xy;
	uv = 2.0 * uv.xy / iResolution.y; 
    uv *= 2.0;

    //Anim
    //uv = RotateUV(uv, iTime * -150.0);
    uv.y += cos(iTime* 0.5) * 0.25;

    //Parameter 
    float blurLvl = 100.0;
    float lineThick = 0.2;
    float lineLenght = 0.65;
    float circleRatio = 0.6;

    // color
    vec3 bgCol = vec3(.0902);
    vec3 orange = Color255(219,125,40);
    vec3 white = vec3(1);    
    vec3 blue = Color255(19,175,240);

    //Draw
    vec3 pixel = bgCol; 
    for(int i; i < 3; i++)
    {
        vec2 uvRot = RotateUV(uv,-45.0 + (45.0 *float(i)));
        float line = sdBox(uvRot + vec2(lineLenght,lineThick-1.0), vec2(lineLenght,0.00));
        pixel = mix(orange,pixel, Clamp01((line-lineThick)*blurLvl));
    }
    float centerCircle = sdCircle(uv + vec2(0.0,0.0), 0.0);
    pixel = mix(orange,pixel, Clamp01((centerCircle - pow(circleRatio,0.0))*blurLvl));
    pixel = mix(white,pixel, Clamp01((centerCircle - pow(circleRatio,1.0))*blurLvl));
    pixel = mix(blue,pixel, Clamp01((centerCircle - pow(circleRatio,2.0))*blurLvl));

	vec4 result = vec4(pixel, 1.0);
	fragColor = result;
}

#define PI 3.14159265359
vec2 RotateUV(inout vec2 uv, float angle)
{	
	angle = angle * PI / 180.0;
    return uv * mat2(cos(angle),sin(angle),-sin(angle),cos(angle));
}
vec3 Color255(int R, int G, int B)
{
    return vec3(float(R)/255.0, float(G)/255.0, float(B)/255.0);
}
float Clamp01( in float value)
{
    return clamp(value,0.0,1.0);
}
float sdCircle( vec2 point, float radius )
{
    return length(point) - radius;
}
float sdBox( in vec2 point, in vec2 box )
{
    vec2 dist = abs(point)-box;
    return length(max(dist,0.0)) + min(max(dist.x,dist.y),0.0);
}
