void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float maxHealth = 80.;
    float currentHealth = 55.;
    float h = currentHealth / maxHealth;

    vec3 bgCol = vec3(0.);
    vec3 topHealthCol = vec3(1., .2, .2);
    vec3 botHealthCol = vec3(.6, 0., 0.);

    vec2 uv = fragCoord/iResolution.xy;


    vec3 col = mix(mix(botHealthCol, topHealthCol, uv.y), bgCol, max(step(h, uv.x), 1. - step(0.6, mod(uv.x * maxHealth, 10.) + 1. - step(0.5, uv.y))));

    fragColor = vec4(col,1.0);
}
