// acid test for Windows/Angle bug in https://www.shadertoy.com/view/fdyfDd

int n = 5;
float a;
  #define F(v)  ( (a = n==5 ? v : v/4.) , a )   // not ok on Windows/Angle, ok on OpenGL
//#define F(v)    (a = n==5 ? v : v/4.)         // ok
//#define F(v)  ( (a = v< 0. ? -v : v ) , a )   // ok 
//#define F(v)  (  a = max(v, -iTime  ) , a )   // ok 

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, U = u/R;

    if (U.y > .5 )                              // top:     () ? :
        O = vec4( U.x < .5 ? F(.5) : F(1.)  );
    else                                        // bottom:  if ; else
        if ( U.x < .5 ) O = vec4( F(.5) );
        else            O = vec4( F(1.) );

    U = floor(u)-floor(R/2.); if (U.x*U.y==0.) O = vec4(1,0,0,1); // red separators     
}
