float Shadow(float time, vec2 uv){

    float Velx = 4.50;
    float Freqx = 1.50;
    float Vely = 4.20;
    float Freqy = 1.50;

    float timeshiftx = (iTime * Velx) +(uv.x * Freqx);
    float timeshifty = (iTime * Vely) +(uv.y * Freqy);

    float shadow = 0.5 + cos(timeshiftx) * sin(timeshifty) * 0.5;
    return shadow;
    }
