vec2 N(float angle) {
    return vec2(sin(angle), cos(angle));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
	uv /= 1.2;
    uv.x = abs(uv.x);

    vec3 col = vec3(0);
    float d;

    float angle = 0.0;
    vec2 n = N((5.0 / 6.0) * 3.1415);

    uv.y += tan((5.0 / 6.0) * 3.1415) * 0.5;
   	d = dot(uv - vec2(0.5, 0), n);
    uv -= max(0.0, d) * n * 2.0;

    float scale = 1.0;

    n = N((2.0 / 3.0) * 3.1415);
    uv.x += 0.5;
    for(int i = 0; i < 5; i++) {
        uv *= 3.0;
        scale *= 3.0;
        uv.x -= 1.5;

        uv.x = abs(uv.x);
        uv.x -= 0.5;
        d = dot(uv, n);
        uv -= min(0.0, d) * n * 2.0;
    }

    //d = length(uv - vec2(clamp(uv.x, -1.0, 1.0), 0));
    //col += smoothstep(1./iResolution.y, .0, d/scale);
    uv /= scale;	

    col += texture(iChannel0, uv * 1.0 - iTime * 0.03).rgb;

    fragColor = vec4(col, 1.0);
}
