#define PI 3.1415926535

float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;

    st.x *= 10.0;
    float column = floor(st.x);
    float fract_x = fract(st.x);

    float rand1 = random(vec2(column));
    float rand2 = random(vec2(column+1.));

    //1
    st.x = rand1;
    //2
    st.x = mix(rand1, rand2, fract_x);
    //3
    st.x = mix(rand1, rand2, smoothstep(0., 1., fract_x));
    //4
    float u1= fract_x * fract_x * (sin(iTime)*2. - (sin(iTime)-1.) * fract_x );
    st.x = mix(rand1, rand2, smoothstep(0., 1., u1));
    //5
    st.x = rand1 + fract_x*(rand2-rand1) + sin(fract_x*2.*PI)*0.2;

    float col = step(st.x, st.y);

    fragColor = vec4(vec3(col),1.0);
}
