#define N(x) fract(sin((x)*333.333))
#define H(t) (cos((vec3(0,2,-2)/3.+t)*6.24)*.5+.5)
#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,r=iResolution,d=normalize(vec3((C-.5*r.xy)/r.y,1.));
    float i=0.,n,g=0.,t=iTime;    
    for(int i=0;i<2;i++) t+=2.*N(floor(t/.8));  
    for(;i++<80.;O+=.01/exp(i*i*n))
    p=d*g-i/1e4,
        p.z-=2.5,
        p=R(p,normalize(H(t*.1)),t),
        g+=n=dot(abs(p),vec3(.577))-.5;
}
