#define PI     3.14159265

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float dotRadius=0.02;
    float dotCenterSize = 0.008;
    float circleRadius = 0.2;
    float intensity= (  0.3 * abs(sin(iTime)) +0.1  )*20.;
    //intensity = abs(sin(iTime)) * 25.0;


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x*= iResolution.x/iResolution.y;


    //Calculate dot position
    float circleScaleTime = 1.0;
    float circleScaleX=cos(iTime*circleScaleTime);
    float circleScaleY=sin(iTime*circleScaleTime);
    circleScaleX=1.0;
    //circleScaleY=1.0;

    float posX=0.0;
    float posY=0.0;

    float waveShift = 0.0;
    vec3 col;
    int numOfDots=8;
    float reach = clamp((cos(iTime*0.5)+1.), 0.01, 3.)*0.01;
    float cAdd=0.;
    for(int i = 0; i < numOfDots; i++){
        waveShift = PI*2.0/float(numOfDots) * float(i);
        if(reach>0. ){
            waveShift*=iTime*0.2;
            cAdd=0.;
        }else if(i%3==0){
            cAdd=0.;
            waveShift+=iTime*0.3*reach;
        }
        posX = circleRadius * cos(iTime*2.0 + waveShift ) * circleScaleX;
        posY = circleRadius * sin(iTime*2.0 + waveShift) * circleScaleY;
        float dot = clamp( 0.0009/( clamp(distance(uv, vec2(posX, posY))-dotCenterSize, 0., 1.) ) , 0., 1.)*intensity;
        col += vec3(dot/6.+cAdd, dot/2., dot);

        //col *= 2.0;
    }

    fragColor= vec4(col,1.0);

    // Output to screen
    //fragColor = vec4(col,1.0);
}
