void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float t = 2.0*sin(iTime);
    mat3 m = mat3(
        vec3(t, 1, 0),
        vec3(0, t, 0),
        vec3(0, 0, 1)
    );
    vec3 xy = vec3(uv, 1);
    vec3 tx = m*xy;

    fragColor = texture(iChannel0, tx.xy);
}
