// click / drag in image left/right to let the ring shrink/grow
#define PI 3.14159265359
float rps = 8.0; // rounds per second
float edgeDelay = 20.0; // positive: grow, negative: shrink

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float edgeDelay = (iMouse.x / iResolution.x - 0.5) * 100.0;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 pos = (fragCoord - 0.5*iResolution.xy)/iResolution.y;
    vec2 uv = pos;


    // ring
    float dist = abs(length(pos) - 0.3);
    float hit = smoothstep(0.05, 0.04, dist);
    float edgeHit = smoothstep(0.9, 0.00, hit);

    // Time varying pixel color
    vec3 fillCol = pow(0.6 + 0.4*sin(iTime*-PI*rps + atan(uv.x, uv.y)), 1.0)*vec3(1);
    vec3 edgeCol = pow(0.6 + 0.4*sin(iTime*-PI*rps + atan(uv.x, uv.y) + (length(pos) - 0.3)*edgeDelay), 1.0)*vec3(1);
    vec3 bgCol = vec3(0.5);

    // Output to screen
    fragColor = vec4(mix(bgCol, mix(fillCol, edgeCol, edgeHit), hit),1.0);
}
