/**
    Simple AA
*/

//#define DEBUG

vec4 edge_detect(in vec2 texcoords)
{
    // texel size
    vec2 texel_size = 1.0 / vec2(textureSize(iChannel0, 0).xy);
    vec3 center = texture(iChannel0, texcoords).rgb;
    vec3 horizontal = texture(iChannel0, texcoords + vec2(texel_size.x, 0)).rgb;
    vec3 vertical = texture(iChannel0, texcoords + vec2(0, texel_size.y)).rgb;

    return vec4(abs(length(center - horizontal)), abs(length(center - vertical)), texel_size.xy * 0.5);
}

void mainImage( out vec4 FragColor, in vec2 FragCoord )
{
    vec2 uv = FragCoord/iResolution.xy;

    vec4 edge = edge_detect(uv);

#ifdef DEBUG
    FragColor = vec4(vec3(edge.xy, 1), 1.0);
#else        
    if(uv.x < 0.5) 
        FragColor = vec4(texture(iChannel0, uv).rgb, 1.0);      
    else
        FragColor = vec4(texture(iChannel0, (uv + (edge.xy * edge.zw))).rgb, 1.0);
#endif
}
