void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalize coords
    vec2 pix = fragCoord.xy / iResolution.xy;

    // use buffer content to determine color mixing
    float fac = texture(iChannel0,pix).x / 10.0 + .1; 
    if (fac <= 0.0) {fac = fac * 0.2;}

    // define colors
    vec4 col1 = vec4(0.752941, 0.733333, 0.780392,1.0);
    vec4 col2 = vec4(0.0745098, 0.192157, 0.258824,1.0);

    // interpolate between colors. Goes darker than the color associated with negative numbers
    // because ripple values can be negative
    vec4 outCol = col1 * fac + col2 * (1.0 - fac);

    fragColor = vec4(outCol);


}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    // sets ripple height at mouse position on click
    if (iFrame %1 == 0 && vec2(fragCoord.xy + .5) == abs(iMouse.xy) && iMouse.z > 0.0)  {

        fragColor = vec4(100);

    }else{

        ivec2 pos = ivec2(fragCoord.xy - 0.5);

        //There's probably a better way to implement kernels on shadertoy, but I don't know what it is
        fragColor = (texelFetch(iChannel0, ivec2(pos.x + 1,pos.y),0) //positives
                     +texelFetch(iChannel0, pos.xy+1,0) * 0.25
                     +texelFetch(iChannel0, ivec2(pos.x,pos.y + 1),0)
                     +texelFetch(iChannel0, pos.xy-1, 0) *0.25//negatives
                     +texelFetch(iChannel0, ivec2(pos.x - 1, pos.y), 0)
                     +texelFetch(iChannel0, ivec2(pos.x, pos.y -1),0)
                     +texelFetch(iChannel0, ivec2(pos.x + 1, pos.y -1),0) *.25//mixed
                     +texelFetch(iChannel0, ivec2(pos.x -1, pos.y + 1),0) *.25

                     // divides total inputs to ensure conservation of energy, 
                     // then subtracts based on the stored volocity value
                     ) /2.49  -texelFetch(iChannel1,pos,0); 

        // dampens energy every tick to make ripples die
        fragColor = fragColor * 0.98;


    }
}

